}

void modified()
{
    super();

    if (! prodRoute.RecId)
    {
        try
        {
            activityToCopy = prodRoute.initFromBaseOperation();
            if (prodRoute.isProjMethodConsumed())
            {
                element.parmSalesPrice(prodRouteProjSetup.ProjSalesPrice);
                element.projJournalTransMapFormSetup().fieldModifiedCategoryIdPost();
                element.projJournalTransMapFormSetup().fieldModifiedCostPricePost();
            }
            if (prodRoute.isProjMethodConsumed())
            {
                element.parmSalesPrice(prodRouteProjProcess.ProjSalesPrice);
                element.projJournalTransMapFormProcess().fieldModifiedCategoryIdPost();
                element.projJournalTransMapFormProcess().fieldModifiedCostPricePost();
            }
            if (prodRoute.isProjMethodConsumed())
            {
                element.parmSalesPrice(prodRouteProjQty.ProjSalesPrice);
                element.projJournalTransMapFormQty().fieldModifiedCategoryIdPost();
                element.projJournalTransMapFormQty().fieldModifiedCostPricePost();
            }
        }
        catch (Exception::Error)
        {
            exceptionTextFallThrough();
        }
    }
}

