    query = this.query();
    qbds = query.dataSourceName(this.name());
    qbds.clearRanges();
    qbds.addRange(fieldNum(ProdRouteProj, ProdId)).value(prodRoute.ProdId);
    qbds.addRange(fieldNum(ProdRouteProj, OprNum)).value(queryValue(prodRoute.OprNum));
    qbds.addRange(fieldNum(ProdRouteProj, OprPriority)).value(queryValue(prodRoute.OprPriority));
    qbds.addRange(fieldNum(ProdRouteProj, CalcType)).value(queryValue(ItemCalcType::Setup));

    super();
}

