public void delete()
{
    if (wrkCtrActivityRequirementSet.RecId)
    {
        //Deletion should be performed on the primary datasource
        prodRoute_ds.delete();
        wrkCtrActivityRequirementSet_ds.executeQuery();
    }
}

public void write()
{
    prodStatusType.updateActivityRequirementSet(wrkCtrActivityRequirementSet,prodRoute);

    if (ProdRouteSplitIntoMultipleJobsFlight::instance().isEnabled() &&
        prodRoute.ProcessTime != prodRoute.orig().ProcessTime)
    {
        prodRoute_ds.write();
    }
}

