{
    element.initActivityDataSource(this);

    super();
}

public void modified()
{
    real     newProcessTime;

    newProcessTime = prodRoute.ProcessTime * lastWrkCtrNumOf / (wrkCtrActivityRequirementSet.Quantity ? wrkCtrActivityRequirementSet.Quantity : 1);
    lastWrkCtrNumOf = wrkCtrActivityRequirementSet.Quantity;

    if (newProcessTime != prodRoute.ProcessTime)
    {
        if (Box::yesNo(strFmt("@SYS23350",prodRoute.ProcessTime,newProcessTime),DialogButton::Yes) == DialogButton::Yes)
        {
            prodRoute.ProcessTime= newProcessTime;
        }
    }

    super();
}

