public void init()
{
    super();

    QueryBuildDataSource qbs = this.query().dataSourceName(prodRouteSchedulingView_ds.name());
    qbs.clearDynalinks();
    qbs.addDynalink(fieldNum(ProdRouteSchedulingView, ProdId), prodRoute, fieldNum(ProdRoute, ProdId));
    qbs.addDynalink(fieldNum(ProdRouteSchedulingView, OprNum), prodRoute, fieldNum(ProdRoute, OprNum));
    qbs.addDynalink(fieldNum(ProdRouteSchedulingView, OprPriority), prodRoute, fieldNum(ProdRoute, OprPriority));
}

void clicked()
{
    WrkCtrId    wrkCtrId;
    WrkCtrTable wrkCtrTable;
    Args args;

    if (rangeWrkCtrId && rangeWrkCtrId.value())
    {
        wrkCtrId = rangeWrkCtrId.value();
    }
    else if (prodRouteSchedulingView.WrkCtrId)
    {
        wrkCtrId = prodRouteSchedulingView.WrkCtrId;
    }

    wrkCtrTable = WrkCtrTable::find(wrkCtrId);

    args = new Args();
    args.record(wrkCtrTable);

    this.menufunction().run(args);
}

