[Form]
public class ProdRouteJobSched extends FormRun
{
    boolean             isCalledFromGantt;
    GanttClient_WrkCtr  ganttClient;
    WrkCtrId            ganttWrkCtrId;
    List                listGanttJobIds;

    ProdJobDispatching  jobScheduling;

    boolean             inActive;

}

void doReSearch()
{
    prodRouteJob_DS.research();
    prodRouteJobSched_DS.research();
}

public void init()
{
    if (!element.args() || !element.args().caller())
    {
        throw error(strfmt("@SYS22678",element.name()));
    }

    if (this.isCalledFromGantt())
    {
        isCalledFromGantt   = true;

        this.initFromGantt();
    }
    else
    {
        if (!element.args().dataset())
        {
            throw error(strfmt("@SYS22678",element.name()));
        }

        isCalledFromGantt   = false;
    }

    super();

    this.initProdJobDispatching();

    if (ProdApplyUserTimezoneOffsetFlight::instance().isEnabled())
    {
        this.showDateTimeWithUserTimezone(ProdParameters::find().ShowSchedDateTimeWithUserTimeZone);
    }
}

private void showDateTimeWithUserTimezone(boolean showWithTimezone)
{
    ProdRouteJob_FromDate.visible(!showWithTimezone);
    ProdRouteJob_FromTime.visible(!showWithTimezone);
    ProdRouteJob_ToDate.visible(!showWithTimezone);
    ProdRouteJob_ToTime.visible(!showWithTimezone);

    ProdRouteJob_FromDateTime.visible(showWithTimezone);
    ProdRouteJob_ToDateTime.visible(showWithTimezone);;
}

void initFromGantt()
{
    ganttClient         = element.args().caller();
    ganttWrkCtrId       = element.args().parm();
    listGanttJobIds     = element.args().parmObject();
}

void initProdJobDispatching()
{
    TmpProdRouteJobSched    tmp;

    tmp.setTmpData(prodRouteJobSched);

    ttsbegin;

    delete_from tmp;

    select prodRouteJobSched;

    jobScheduling = new ProdJobDispatching(prodRouteJobSched);

    ttscommit;
}

void insertJobsFromGantt()
{
    ProdRouteJob    prodRouteJobTmp;
    ListEnumerator  listEnumerator;

    Set             setProdIds = new Set(Types::String);

    if (listGanttJobIds == null)
    {
        throw error(strfmt("@SYS27147", classId2Name(classidget(this))));
    }

    listEnumerator = listGanttJobIds.getEnumerator();

    while (listEnumerator && listEnumerator.moveNext())
    {
        prodRouteJobTmp = ProdRouteJob::findJobId(listEnumerator.current());

        if (prodRouteJobTmp.RecId && !setProdIds.in(prodRouteJobTmp.ProdId))
        {
            setProdIds.add(prodRouteJobTmp.ProdId);

            ProdRouteJob_DS.markRecord(prodRouteJobTmp, 1);
        }
    }
}

public boolean isCalledFromGantt()
{
    if (!SysDictClass::isEqualOrSuperclass(classidget(element.args().caller()), classnum(GanttClient_WrkCtr)))
    {
        return false;
    }

    return true;
}

public void run()
{
    super();

    if (isCalledFromGantt)
    {
        element.insertJobsFromGantt();
    }
}

void initQuery()
{
    QueryBuildDataSource ds = this.query().dataSourceNo(1);

    if (isCalledFromGantt)
    {
        ds.addRange(fieldnum(ProdRouteJob, WrkCtrId)).value(queryValue(ganttWrkCtrId));
    }

    ds.addRange(fieldnum(ProdRouteJob,JobStatus)).value('!'+queryValue(ProdJobStatus::Completed));
    ds.addRange(fieldnum(ProdRouteJob,JobFinished)).value(queryValue(NoYes::No));
    ds.addRange(fieldnum(ProdRouteJob,JobType)).value('!'+queryValue(RouteJobType::QueueBefore)+','+
                                                                                '!'+queryValue(RouteJobType::Overlap)+','+
                                                                                '!'+queryValue(RouteJobType::Transport)+','+
                                                                                '!'+queryValue(RouteJobType::QueueAfter));

    ds.addRange(fieldnum(ProdRouteJob,RecId)).value(
        strFmt(
            '((%1.%2 != %3) || (%1.%4 == %5))',
            ds.name(),
            fieldStr(ProdRouteJob, SchedCancelled),
            enum2int(NoYes::Yes),
            fieldStr(ProdRouteJob, JobType),
            enum2int(RouteJobType::Process)));

    if (element.args().caller().name() == formstr(JmgProdStatusListPage))
    {
        ds.clearDynalinks();

        MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::createFromCaller(element.args().caller());
        multiSelectionHelper.createQueryRanges(ds, fieldstr(JmgJobTable, JobId));
    }

    ds.sortClear();
    ds.addSortField(fieldnum(ProdRouteJob, FromDate));
    ds.addSortField(fieldnum(ProdRouteJob, FromTime));

    ds.addSortIndex(indexnum(ProdRouteJob,WrkCtrIdx));
    ds.indexIsHint(true);
}

void init()
{
    super();

    this.initQuery();
}

