{
    Query  q = new Query();

    q.addDataSource(tablenum(TmpProdRouteJobSched));
    q.dataSourceNo(1).addSortField(fieldnum(TmpProdRouteJobSched,LineNum));

    this.query(q);

    super();
}

int active()
{
    ProdRouteJob prodRouteJobLocal;
    int          ret;

    ret = super();

    if (   !inActive
        && scheduleTab.isActivePage()
        && prodRouteJobSched.JobId)
    {
        inActive = true;
        prodRouteJobLocal = ProdRouteJob::findJobId(prodRouteJobSched.JobId);
        prodRouteJob_DS.positionToRecord(prodRouteJobLocal);
        inActive = false;
    }

    return ret;
}

