public void pageActivated()
{
    ProdRouteJob    prodRouteJobLocal;

    super();

    element.initProdJobDispatching();

    prodRouteJobLocal  = ProdRouteJob_DS.getFirst(1);

    while (prodRouteJobLocal)
    {
        jobScheduling.insertProdRouteJob(prodRouteJobLocal);
        prodRouteJobLocal = ProdRouteJob_DS.getNext();
    }

    prodRouteJob_ds.refresh();
    prodRouteJobSched_ds.research();
}

void clicked()
{
    super();

    jobScheduling.sortTmpUp(prodRouteJobSched);
}

void clicked()
{
    jobScheduling.sortTmpDown(prodRouteJobSched);

    super();
}

void clicked()
{
    if (isCalledFromGantt && ganttClient)
    {
        jobScheduling.scheduleJobs(ganttClient);
    }
    else
    {
        jobScheduling.scheduleJobs();
    }

    super();

    element.doReSearch();
}
