public class ProdRouteOprOverview extends FormRun implements WrkCtrIResourceRequirement
{
    #resAppl

    ProdRouteTransCache           prodRouteTransCache;
    ProdRouteOprOverviewForm      prodRouteOprOverviewForm;

    PmfFormCtrl_ProdRouteOprOverview_Prod   pmfFormCtrl_ProdRouteOprOverview_Prod;
    PmfFormCtrl_ProdRouteOprOverview_Route  pmfFormCtrl_ProdRouteOprOverview_Route;
}

void init()
{
    pmfFormCtrl_ProdRouteOprOverview_Prod = PmfFormCtrl_ProdRouteOprOverview_Prod::newFromForm(element);
    pmfFormCtrl_ProdRouteOprOverview_Route = PmfFormCtrl_ProdRouteOprOverview_Route::newFromForm(element);

    super();

    prodRouteTransCache = ProdRouteTransCache::constructOpr();

    prodRouteOprOverviewForm = ProdRouteOprOverviewForm::construct();
    prodRouteOprOverviewForm.prodRouteDSInitPost(prodRoute);

    pmfFormCtrl_ProdRouteOprOverview_Prod.setDatasources(prodTable);
    pmfFormCtrl_ProdRouteOprOverview_Route.setDatasources(prodRoute);
    pmfFormCtrl_ProdRouteOprOverview_Route.initPost();

    element.setButtonAccess();

    if (ProdApplyUserTimezoneOffsetFlight::instance().isEnabled())
    {
        this.showDateTimeWithUserTimezone(ProdParameters::find().ShowSchedDateTimeWithUserTimeZone);
    }
}

private void showDateTimeWithUserTimezone(boolean showWithTimezone)
{
    ProdRoute_FromDate.visible(!showWithTimezone);
    ProdRoute_FromTime.visible(!showWithTimezone);
    ProdRoute_ToDate.visible(!showWithTimezone);
    ProdRoute_ToTime.visible(!showWithTimezone);

    ProdRoute_FromDateTime.visible(showWithTimezone);
    ProdRoute_ToDateTime.visible(showWithTimezone);
}

public void initActivityDataSource(FormDataSource _formDataSource)
{
    SysDictTable sysDictTable = new SysDictTable(_formDataSource.table());

    if (!isConfigurationkeyEnabled(sysDictTable.configurationKeyId()))
    {
        _formDataSource.linkType(FormLinkType::Passive);
    }
}

