}

/// <summary>
///     Initializes the <c>ProdRouteSchedulingView</c> data source.
/// </summary>
public void init()
{
    QueryBuildDataSource qbs;

    super();

    qbs = this.query().dataSourceName(prodRouteSchedulingView_ds.name());

    qbs.clearLinks();

    qbs.addLink(fieldnum(ProdRoute, ProdId), fieldnum(ProdRouteSchedulingView, ProdId));
    qbs.addLink(fieldnum(ProdRoute, OprNum), fieldnum(ProdRouteSchedulingView, OprNum));
    qbs.addLink(fieldnum(ProdRoute, OprPriority), fieldnum(ProdRouteSchedulingView, OprPriority));
}

