public void init()
{
    QueryBuildDataSource qbdsWrkCtrResourceAndGroupView;

    super();

    qbdsWrkCtrResourceAndGroupView = this.query().dataSourceName(wrkCtrResourceAndGroupView_ds.name());

    qbdsWrkCtrResourceAndGroupView.clearLinks();
    qbdsWrkCtrResourceAndGroupView.clearRanges();
    qbdsWrkCtrResourceAndGroupView.addLink(fieldnum(ProdRouteSchedulingView, WrkCtrId), fieldnum(WrkCtrResourceAndGroupView, WrkCtrId), 'ProdRouteSchedulingView');

    qbdsWrkCtrResourceAndGroupView.addRange(fieldnum(WrkCtrResourceAndGroupView,ValidFrom)).value(
            strfmt('(%1.%2 <= %3.%4)',
                    wrkCtrResourceAndGroupView_ds.name(),
                    fieldstr(WrkCtrResourceAndGroupView,ValidFrom),
                    prodRouteSchedulingview_ds.name(),
                    fieldstr(ProdRouteSchedulingView,FromDate))
    );

    qbdsWrkCtrResourceAndGroupView.addRange(fieldnum(WrkCtrResourceAndGroupView,ValidTo)).value(
            strfmt('(%1.%2 >= %3.%4)',
                    wrkCtrResourceAndGroupView_ds.name(),
                    fieldstr(WrkCtrResourceAndGroupView,ValidTo),
                    prodRouteSchedulingview_ds.name(),
                    fieldstr(ProdRouteSchedulingView,FromDate))
    );
}

