    qbs.addLink(fieldnum(ProdRoute, OprPriority), fieldnum(wrkCtrResourceDisplayType, OprPriority));                        
    qbs.addLink(fieldnum(ProdRouteSchedulingView, WrkCtrId), fieldnum(wrkCtrResourceDisplayType, WrkCtrId), 'ProdRouteSchedulingView' );
}

public void clicked()
{
    //
    // ProdTable data source has LinkType passive in order to avoid sending excessive select statements to the database
    // while surfing through operations.
    // Therefore, ProdTable data source has to be manually updated when one of journal buttons is clicked.
    // Thus, select statement to ProdTable is sent only when opening a journal form.
    //
    prodTable_DS.executeQuery();

    super();
}

