Method.Source:
[Form]
public final class ProdRouteTrans extends FormRun
{
    WrkCtrTable   wrkCtrTable;

    #define.formParmValueResources("Resources")
    #define.formParmValueSubcontracting("Subcontracting")
}

public void enableButtons()
{
    boolean enableButton = this.hasTrackingDimensionsEnabled();

    inventTrackingDimTracing.enabled(enableButton);

    boolean hasRelatedJournalRecord = prodRouteTrans.VoucherCalc != '';
    LedgerTransVoucherCosting.enabled(hasRelatedJournalRecord);
}

public boolean hasTrackingDimensionsEnabled()
{
    ProdTable   prodTable;
    boolean     hasTrackingDimensionsEnabled;

    if (prodRouteTrans.TransRefType == ProdTransRefType::Production)
    {
        prodTable = ProdTable::find(prodRouteTrans.TransRefId);
        hasTrackingDimensionsEnabled = InventDimParm::activeDimFlag(InventDimGroupSetup::newItemId(prodTable.ItemId)).anyTrackingDimensionFlagSet();
    }

    return hasTrackingDimensionsEnabled;
}

public void init()
{
    super();

    if (this.showingProductionFlowRelatedTransactions())
    {
        feedback.visible(NoYes::No);
    }

    if (ProdApplyUserTimezoneOffsetFlight::instance().isEnabled())
    {
        this.showTimeWithUserTimezone(ProdParameters::find().ShowSchedDateTimeWithUserTimeZone);
    }
}

private void showTimeWithUserTimezone(boolean showWithTimezone)
{
    Job.visible(!showWithTimezone);
    JobTimezone.visible(showWithTimezone);
}

public boolean showingProductionFlowRelatedTransactions()
{
    return element.args()
        && (element.args().dataset() == tablenum(PlanReference)
        ||  element.args().dataset() == tablenum(LeanProductionFlow)
        ||  element.args().dataset() == tablenum(LeanProductionFlowActivity)
        ||  element.args().dataset() == tablenum(LeanProductionFlowReference));
}

public void linkActive()
{
    LeanProductionFlowCosting   productionFLowCosting;
    ProdCalcTrans               prodCalcTrans;

    QueryBuildRange             qbrTransRefId;
    QueryBuildRange             qbrVoucher;
    QueryBuildRange             qbrState;
    QueryBuildRange             qbrServiceItemId;
    QueryBuildRange             qbrResource;

    if (element.args().dataset() == tableNum(ProdCalcTrans))
    {
        prodCalcTrans = element.args().record();

        qbrTransRefId = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdRouteTrans)), fieldNum(ProdRouteTrans, TransRefId));
        qbrState = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdRouteTrans)), fieldNum(ProdRouteTrans, Calculated));
        qbrVoucher = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdRouteTrans)), fieldNum(ProdRouteTrans, VoucherCalc));
        qbrServiceItemId = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdRouteTrans)), fieldNum(ProdRouteTrans, ServiceItemId));
        qbrResource = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(ProdRouteTrans)), fieldNum(ProdRouteTrans, WrkCtrId));

        switch (prodCalcTrans.TransRefType)
        {
            case ProdTransRefType::Production :
                qbrTransRefId.value(prodCalcTrans.TransRefId);
                qbrState.value(SysQuery::valueUnlimited());
                qbrVoucher.value(SysQuery::valueUnlimited());
                break;
            case ProdTransRefType::ProductionFlow :
                qbrTransRefId.value(prodCalcTrans.TransRefId);
                qbrState.value(queryValue(NoYes::No));
                qbrVoucher.value(SysQuery::valueEmptyString());
                break;
            case ProdTransRefType::BackflushCosting :
                productionFLowCosting = LeanProductionFlowCosting::find(str2int64(prodCalcTrans.TransRefId));
                qbrTransRefId.value(productionFLowCosting.displayProductionFlowName());
                qbrState.value(queryValue(NoYes::Yes));
                qbrVoucher.value(productionFLowCosting.Voucher);
                break;
        }

        if (prodCalcTrans.CalcType == ItemCalcType::Service)
        {
            qbrServiceItemId.value(prodCalcTrans.Resource);
            qbrResource.value(SysQuery::valueEmptyString());
        }
        else
        {
            qbrResource.value(prodCalcTrans.Resource);
            qbrServiceItemId.value(SysQuery::valueEmptyString());
        }
    }

    super();

    if (element.args().caller() && element.args().dataset() == tableNum(WrkCtrTable))
    {
        wrkCtrTable = element.args().record();
    }
}

public void init()
{
    QueryBuildDataSource    qbdsProdRouteTrans;
    str                     formParmValue = element.args().parm();

    super();

    if (element.args().caller() && element.args().dataset() == tableNum(WrkCtrTable))
    {
        wrkCtrTable = element.args().record();
        if (wrkCtrTable.IsIndividualResource)
        {
            // Clear dynalinks and only create link to the WrkCtrId field
            qbdsProdRouteTrans = this.query().dataSourceTable(tableNum(ProdRouteTrans));
            qbdsProdRouteTrans.clearDynalinks();
            qbdsProdRouteTrans.clearRanges();

            qbdsProdRouteTrans.addDynalink(fieldNum(ProdRouteTrans,WrkCtrId), wrkCtrTable, fieldNum(WrkCtrTable, WrkCtrId));
        }
    }

    if (element.showingProductionFlowRelatedTransactions() && formParmValue)
    {
        // This form is used to display lean production flow realted cost transactions
        // for resource consumptions or for subcontracted activities. The following lines
        // adds a range to filter transactions according to what the form is supposed to display.
        // Once the form is opened, it will always display the same type of transactions, so we
        // don't want to use a dyna-link here.
        qbdsProdRouteTrans = this.query().dataSourceTable(tableNum(ProdRouteTrans));
        if (formParmValue == #formParmValueResources)
        {
            qbdsProdRouteTrans.addRange(fieldNum(ProdRouteTrans, WrkCtrId)).value(SysQuery::valueNotEmptyString());
        }
        else if (formParmValue == #formParmValueSubcontracting)
        {
            qbdsProdRouteTrans.addRange(fieldNum(ProdRouteTrans, ServiceItemId)).value(SysQuery::valueNotEmptyString());
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    project.visible(prodRouteTrans.isProjMethodConsumed());
    if (prodRouteTrans.isProjMethodConsumed())
    {
        projectHourCostPrice.visible(prodRouteTrans.TransType == ProdRouteTransType::Time);
        projectQuantityCostPrice.visible(prodRouteTrans.TransType == ProdRouteTransType::Qty);
    }

    element.enableButtons();

    return ret;
}
