[Form]
public class ProdSchedule extends FormRun
{
    ProdMultiScheduling         controller;
    QueryBuildRange             parmQuery;
    boolean                     isJobScheduling;
    Set                         userModifiedRecords;
    boolean                     defaultsModified;

}

public void init()
{
    userModifiedRecords = new Set(Types::Int64);

    controller = element.args().caller().runbase() as ProdMultiScheduling;
    isJobScheduling = controller is ProdMultiSchedulingJob;

    super();

    if (isJobScheduling)
    {
        element.design().caption("@SYS16375");
        OperationsScheduling.visible(false);
        OperationsSchedulingDefaults.visible(false);
        OperationsScheduleSelectOrdersButton.visible(false);
    }
    else
    {
        element.design().caption("@SYS10062");
        JobScheduling.visible(false);
        JobSchedulingDefaults.visible(false);
        JobScheduleSelectOrdersButton.visible(false);
    }

    ProdParmSchedulingDefault_ds.create();
    ProdParmSchedulingDefault_ds.allowCreate(false);

    element.enableDisableFields(ProdParmScheduling_ds, ProdParmScheduling);
    element.enableDisableFields(ProdParmSchedulingDefault_ds, ProdParmSchedulingDefault);

    element.addDefaultsModifiedEventHandlers();
}

protected void addDefaultsModifiedEventHandlers()
{
    this.wireDefaultsModifiedEventHandlers(true);
}

protected void removeDefaultsModifiedEventHandlers()
{
    this.wireDefaultsModifiedEventHandlers(false);
}

private void wireDefaultsModifiedEventHandlers(boolean _add)
{
    DictTable dictTable = new DictTable(ProdParmSchedulingDefault_ds.table());
    int fieldCount = dictTable.fieldCnt();
    for (int i = 1; i <= fieldCount; i++)
    {
        FormDataObject dataObject = ProdParmSchedulingDefault_ds.object(dictTable.fieldCnt2Id(i));
        if (dataObject)
        {
            if (_add)
            {
                dataObject.OnModified += eventhandler(this.defaultsModifiedEventHandler);
            }
            else
            {
                dataObject.OnModified -= eventhandler(this.defaultsModifiedEventHandler);
            }
        }
    }
}

protected void defaultsModifiedEventHandler(FormDataObject _sender, FormDataFieldEventArgs _e)
{
    defaultsModified = true;
}

public void propagateDefaults(boolean _forcePropagation = false)
{
    if (defaultsModified || _forcePropagation)
    {
        ParmId parmId = controller.parmId();
        ProdParmScheduling localParm, savedProdParmSchedulingDefault;
        ProdId currProdId;

        Global::buf2Buf(controller.parmStandardParm(), savedProdParmSchedulingDefault);

        // this will apply currently displayed defaults
        controller.parmStandardParm(ProdParmSchedulingDefault);

        ttsbegin;

        while select forupdate localParm
            where localParm.ParmId == parmId
        {
            if (!userModifiedRecords.in(localParm.RecId))
            {
                controller.initParmBuffer(localParm);
                localParm.update();
            }
        }

        ttscommit;

        // recover saved defaults
        controller.parmStandardParm(savedProdParmSchedulingDefault);

        this.doReselect();
    }

    defaultsModified = false;
}

protected void enableDisableFields(FormDataSource _ds, ProdParmScheduling _record)
{
    _ds.object(fieldNum(ProdParmScheduling, SyncRef)).allowEdit(_record.SchedRef);
    _ds.object(fieldNum(ProdParmScheduling, ApplyIssueMargin)).allowEdit(_record.SchedRef);
    _ds.object(fieldNum(ProdParmScheduling, ApplyReceiptMargin)).allowEdit(_record.SchedRef);

    if (isJobScheduling)
    {
        _ds.object(fieldNum(ProdParmScheduling,WrkCtrIdSched)).allowEdit(_record.JobId ? true : false);
        _ds.object(fieldNum(ProdParmScheduling, ScheduleSingleJob)).allowEdit(_record.JobId ? true : false);
    }
}

protected void orderSelectionChanged()
{
    //Reread is needed because query selection dialog might have updated the record.
    ProdParmUpdate_ds.reread();

    element.propagateDefaults(true);
    ParamsPerOrderTabPage.activatePage();
}

public void doSorting(ProdSchedulingSortType _sortingType)
{
    if (ProdParmScheduling.validateWrite())
    {
        ProdParmScheduling.write();

        ProdParmSchedulingSort::runSorting(ProdParmScheduling.ParmId, _sortingType);
        this.doReselect();
    }
}

RunBase runBase()
{
    return controller;
}

public void closeOk()
{
    if (prodParmScheduling.ParmId && prodParmScheduling.ProdId)
    {
        ProdParmScheduling_ds.write();
    }

    this.propagateDefaults();

    if (controller.checkCloseDialog())
    {
        super();

        if (ProdParmUpdate.LateSelection)
        {
            controller.setLateSelectionParm(ProdParmSchedulingDefault);
        }
    }
}

void doReselect()
{
    ProdParmScheduling  prodParmSchedulingTmp;

    select firstonly prodParmSchedulingTmp
        where prodParmSchedulingTmp.ParmId == ProdParmScheduling.ParmId
           && prodParmSchedulingTmp.ProdId == ProdParmScheduling.ProdId;

    prodParmScheduling_ds.executeQuery();

    if (prodParmSchedulingTmp)
    {
        prodParmScheduling_ds.positionToRecord(prodParmSchedulingTmp);
    }
}

void save()
{
    Global::buf2Buf(ProdParmSchedulingDefault, controller.parmStandardParm());
    controller.saveLast();
}

public void init()
{
    super();

    ProdParmScheduling_ds.cacheAddMethod(tableMethodStr(ProdParmScheduling, prodName));

    parmQuery = ProdParmScheduling_ds.queryBuildDataSource().addRange(fieldNum(ProdParmScheduling, ParmId));
}

public void executeQuery()
{
    if (controller.parmId())
    {
        parmQuery.value(queryValue(controller.parmId()));
    }
    else
    {
        parmQuery.value(SysQuery::valueEmptyString());
    }

    super();
}

public int active()
{
    int ret;

    ret = super();

    element.enableDisableFields(ProdParmScheduling_ds, ProdParmScheduling);

    return ret;
}

public void write()
{
    RecVersion prevRecVersion = ProdParmScheduling.RecVersion;

    super();

    if (ProdParmScheduling.RecVersion != prevRecVersion)
    {
        userModifiedRecords.add(ProdParmScheduling.RecId);
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    ProdRouteJob::lookupJobId(_formControl, ProdParmScheduling.ProdId, NoYes::Yes);
}

public void modified()
{
    super();

    if (!ProdParmScheduling.JobId)
    {
        ProdParmScheduling.ScheduleSingleJob = NoYes::No;
    }
    else if (!ProdParmScheduling.WrkCtrIdSched)
    {
        ProdParmScheduling.WrkCtrIdSched = ProdRouteJob::findJobId(ProdParmScheduling.JobId).WrkCtrId;
    }

    element.enableDisableFields(ProdParmScheduling_ds, ProdParmScheduling);
}

public void lookup(FormControl _formControl, str _filterStr)
{
    ProdParmScheduling::prodLookUpJob(_formControl);
}

public void modified()
{
    super();

    if (!ProdParmScheduling.SchedRef)
    {
        ProdParmScheduling.SyncRef = false;
        ProdParmScheduling.ApplyIssueMargin = false;
        ProdParmScheduling.ApplyReceiptMargin = false;
    }

    element.enableDisableFields(ProdParmScheduling_ds, ProdParmScheduling);
}

public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldnum(ProdParmUpdate, ParmId)).value(queryValue(controller.parmId()));
}

public void modified()
{
    super();

    prodParmUpdate.update();
    prodParmUpdate_ds.refresh();
}

public void write()
{
    // do not call super()
}

public boolean validateWrite()
{
    return true;
}

public void create(boolean _append = false)
{
    super(_append);
    ProdParmSchedulingDefault.data(controller.parmStandardParm());
    ProdParmSchedulingDefault.ParmID = controller.parmId();
}

public void modified()
{
    super();

    if (!ProdParmSchedulingDefault.SchedRef)
    {
        ProdParmSchedulingDefault.SyncRef = false;
        ProdParmSchedulingDefault.ApplyIssueMargin = false;
        ProdParmSchedulingDefault.ApplyReceiptMargin = false;
    }

    element.enableDisableFields(ProdParmSchedulingDefault_ds, ProdParmSchedulingDefault);
}

public void pageActivated()
{
    super();

    // event handlers are sometimes lost when moving out of the tab, so it's necessary to wire them again
    element.addDefaultsModifiedEventHandlers();
}

public boolean allowPageDeactivate()
{
    boolean allowDeactivate = super();
    if (allowDeactivate)
    {
        element.propagateDefaults();

        // event handlers are sometimes not lost, unwiring to prevent multiple subscriptions
        element.removeDefaultsModifiedEventHandlers();
    }

    return allowDeactivate;
}

public void clicked()
{
    element.save();
}

public boolean allowPageDeactivate()
{
    boolean allowDeactivate = super() 
                              && prodParmScheduling.ParmId 
                              && prodParmScheduling.ProdId 
                              && ProdParmScheduling_ds.validateWrite();

    if (allowDeactivate)
    {
        ProdParmScheduling_ds.write();
    }

    return allowDeactivate;
}

