public void setGanttColor()
{
    GanttColorId bgColor = prodTable.GanttColorId;

    if (WHSProdTable::find(prodTable.ProdId).Stopped)
    {
        bgColor  = WinAPI::RGB2int(#WHSPastelRed);
    }

    GanttColorId fgColor = GanttReadableColorProvider::getReadableTextColor(bgColor);

    ProdTable_GanttColor.backgroundColor(bgColor);
    ProdTable_GanttColor.foregroundColor(fgColor);
}

public void doFormCache(container _packRecs)
{
    Container mapContainer = conPeek(_packRecs, 1);
    int updatedRecordCount = 0;

    if (mapContainer != conNull())
    {
        updatedRecordCount = Map::create(mapContainer).elements();
    }

    RunBaseMultiParmApplication::formCacheAll(_packRecs,element,prodTable,true,inventDim);  

    if (ProdTable::countCollectReferences(prodTable.ProdId) > 1 || updatedRecordCount > 1)
    {
        element.args().lookupRecord(ProdTable::find(prodTable.ProdId));
        prodTable_DS.research(false);
    }
    else
    {
        element.doReread();
    }
}

public void doReread(
    container updatedRecsCon = conNull(),
    container createdRecsCon = conNull(),
    container deletedRecsCon = conNull()
    )
{
    prodTable_DS.reread();
    prodTable_DS.refresh();
    prodTable_DS.active();
}

void enableControlsActive()
{
    boolean hasData;
    boolean isWhsEnabled;

    prodTableForm.setDatasources(prodTable, inventDim, prodTableProjError, prodTableProjOver, prodTableProjUnder);
    hasData = prodTableForm.checkHasData();

    manageGroup.enabled(hasData);
    actions.enabled(hasData);
    manageInvent.enabled(hasData);
    orderDetails.enabled(hasData);
    schedule.enabled(hasData);
    view.enabled(hasData);
    manageQuality.enabled(hasData);        

    //Register other button rules
    viewGroup.enabled(prodTableForm.checkGanttEnabled());
    manageQuality.visible(prodTableForm.checkQualityManagementEnabled());
    costControlCommittedCost.enabled(prodTableForm.checkIsProj());
    changeGroup.visible(prodTableForm.checkChangeGroupEnabled());

    if (prodTable)
    {
        prodStatus = prodTable.statusClient();

        prodTableForm.updateDesign(InventDimFormDesignUpdate::Active);

        tab_General.allowEdit(prodStatus.formFieldsActive());
        tab_SetUp.allowEdit(prodStatus.formFieldsActive());
        tab_References.allowEdit(prodStatus.formFieldsActive());
        tab_UpdateNow.allowEdit(prodStatus.formFieldsActive());

        prodTable_ds.clearDisplayOption(prodTable);

        //Production order actions
        costEstimationButton.enabled(prodStatus.runCostEstimation(null,true));
        prodSchedulingOperation.enabled(prodStatus.runOperationScheduling(null,true));
        prodSchedulingJob.enabled(prodStatus.runJobScheduling(null,true));
        releaseButton.enabled(prodStatus.runRelease(null,true));
        startUpButton.enabled(prodStatus.runStartUp(null,true));
        reportFinishedButton.enabled(prodStatus.runReportFinished(null,true));
        endButton.enabled(prodStatus.runHistoricalCost(null,true));
        statusDecreaseButton.enabled(prodStatus.runStatusDecrease(null,true));
        splitButton.enabled(prodStatus.runSplit(null,true));

        boolean isPDSBalanceProdBOMEnhancedFeatureEnabled = PDSBalanceProdBOMEnhancedFeature::instance().isEnabled();
        if (isPDSBalanceProdBOMEnhancedFeatureEnabled)
        {
            pdsBatchBalancing.visible(false);
            pdsBalanceProdBOMEnhanced.visible(true);
            pdsBalanceProdBOMEnhanced.enabled(prodStatus.pdsCanBeBatchBalanced());            
        }
        else
        {
            pdsBalanceProdBOMEnhanced.visible(false);
            pdsBatchBalancing.enabled(prodStatus.pdsCanBeBatchBalanced());
        }            

        boolean isStdCost = InventModelGroupItem::isStandardCost(prodTable.ItemId);
        stdCostTransactions.enabled(isStdCost);
        ProdCalcVarianceTrans.enabled(isStdCost && prodTable.ProdStatus == ProdStatus::Completed);

        CostComparisonButton.enabled(prodTable.ProdStatus == ProdStatus::Completed);
    }

    isWhsEnabled = prodTable && WHSInventEnabled::exist(prodTable.ItemId);

    whsProdTable_Stopped.enabled(isWhsEnabled);
    whsProdTable_LastPallet.enabled(isWhsEnabled);
    whsProdTable_ProdQty.enabled(isWhsEnabled);
    whsProdTable_CrossDock.enabled(WHSInventEnabled::exist(prodTable.ItemId));
}

