void init()
{
    prodTableForm = ProdTableForm::newForm(element);

    traceFilter = element.args().parmObject() as InventTrackingTraceFilter;
    if (traceFilter)
    {
        traceFilter.parmFormRun(this);
    }

    element.pmfCustomizeArgsPreInitSuper();

    pmfFormCtrl_ProdTable_ProdTable = PmfFormCtrl_ProdTable_ProdTable::newFromForm(element);
    pmfFormCtrl_ProdTable_InventDim = PmfFormCtrl_ProdTable_InventDim::newFromForm(element);

    pmfFormCtrl_ProdTable_ProdTable.initPre();
    pmfFormCtrl_ProdTable_InventDim.initPre();

    super();

    detailsNavList.visible(this.showNavigationList());

    pmfFormCtrl_ProdTable_ProdTable.setDatasources(prodTable);
    pmfFormCtrl_ProdTable_InventDim.setDatasources(inventDim, prodTable);

    pmfFormCtrl_ProdTable_ProdTable.initPost();
    pmfFormCtrl_ProdTable_InventDim.initPost();

    this.initMultiSiteFilter();

    // Initialization specifically for projects
    // Only show production orders associated to projects if form is called from the project main menu
    if (element.args().parmEnumType() == enumNum(ProdTableFormId) && element.args().parmEnum() == ProdTableFormId::Project)
    {
        if (! element.args().record()) // called from menu
            prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable, ProjId)).value(SysQuery::valueNotEmptyString());
    }

    switch (element.args().menuItemName())
    {
        case menuItemDisplayStr(CostProdTableDetailsList):
            NewProductionBatchOrder.visible(false);
            break;

        case menuItemDisplayStr(CostPmfProdTableDetailsList):
            NewProductionOrder.visible(false);
            break;
    }

    prodTableForm.updateDesign(InventDimFormDesignUpdate::Init);
    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    element.enableControlsActive();
    element.setmandatory();

    if (traceFilter)
    {
        traceFilter.formInitPost();
        this.updateDesignForItemTracing();
    }

    ProdTable_GanttColor.colorScheme(FormColorScheme::RGB);

    // <GTE>
    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    if (ProdApplyUserTimezoneOffsetFlight::instance().isEnabled())
    {
        this.showDateTimeWithUserTimezone(ProdParameters::find().ShowSchedDateTimeWithUserTimeZone);
    }
}

private void showDateTimeWithUserTimezone(boolean showWithTimezone)
