    DateScheduled.visible(!showWithTimezone);
    DateScheduledTimezone.visible(showWithTimezone);
}

/// <summary>
/// Enable and show filter for Site if multisite functionality is activated; otherwise, disable and hide filter.
/// </summary>
/// <remarks>
/// Site filter is disabled and hidden if the form is called from another form as "jump to maintable", and
/// the framework finds an active valid relation between the prodTable datasource and the calling record hold
/// by the args object.
/// </remarks>
void initMultiSiteFilter()
{
    Query                   q;

    q = prodTable_ds.query();

    queryBuildRangeSiteId = q.dataSourceTable(tableNum(InventDim)).findRange(fieldNum(InventDim,InventSiteId));
    if (!queryBuildRangeSiteId)
        queryBuildRangeSiteId = q.dataSourceTable(tableNum(InventDim)).addRange(fieldNum(InventDim,InventSiteId));

    queryBuildRangeSiteId.status(RangeStatus::Locked);

    queryBuildRangeSiteId.value(SysQuery::valueUnlimited());
}

