/// <summary>
///     Adjusts the arguments with which this form was opened for process and SSRS functionality extensions.
/// </summary>
/// <remarks>
///     The drilldown from SSRS reports calls the <c>formRef</c> function without record parameters.
///     It is needed to fetch the ID value and attempt to locate a record.
/// </remarks>
protected void pmfCustomizeArgsPreInitSuper()
{
    xArgs               arguments       = element.args();
    ProdTable           prodTableArgumentInjection;
    FormStringControl   callerControl;
    ProdId              prodId;

    if (#pmfEnabled && (arguments.caller() != null))
    {
        //determine whether the caller is of type SSRS-drilldown
        if (!arguments.record() && SysFormRun::isCalledFromJumpRef(arguments, fieldNum(ProdTable, ProdId)))
        {
            //drill-down will not have a caller record, but we can get the ID from the lookup value
            prodId = arguments.lookupValue();
            if (!prodId)
            {
                callerControl = SysTableLookup::getCallerStringControl(arguments);
                if (callerControl != null)
                {
                    //this is a jumpref from an ID field
                    prodId = callerControl.text();
                }
            }
            if (prodId)
            {
                prodTableArgumentInjection = ProdTable::find(prodId);
            }
        }
        else if (arguments.record() is ProdTable)
        {
            prodTableArgumentInjection = arguments.record();
        }

        if (prodTableArgumentInjection)
        {
            arguments.record(prodTableArgumentInjection);
            if (prodTableArgumentInjection.ProdType == ProdType::Process)
            {
                //open in batch mode context
                arguments.menuItemType(MenuItemType::Display);
                arguments.menuItemName(menuitemDisplayStr(PmfProdProcessBatch));
            }
        }
    }
}

