void setmandatory()
{
    if (prodTable)
    {
        tab_Project.visible(prodTable.isProjMethodLinkedToOrder() || prodTable.isProjMethodFinished() || prodTable.isProjMethodConsumed());

        project.visible(prodTable.isProjMethodLinkedToOrder() || prodTable.isProjMethodFinished() || prodTable.isProjMethodConsumed());
        projectIdentification_DlvDate.visible(prodTable.isProjMethodFinished());
        projectIdentification_ProjCategoryId.visible(prodTable.isProjMethodFinished());
        projectIdentification_ItemId.visible(prodTable.isProjMethodFinished());
        projectIdentification_ProjLinePropertyId.visible(prodTable.isProjMethodFinished());
        projectTransaction.visible(prodTable.isProjMethodFinished());
        projectCostPrice.visible(prodTable.isProjMethodFinished());
        projectSalesPrice.visible(prodTable.isProjMethodFinished());
        projectSalestax.visible(prodTable.isProjMethodFinished());

        project.allowEdit(prodStatus.formFieldsActive());
        projectIdentification_DlvDate.allowEdit((prodTable.isProjMethodLinkedToOrder() || prodTable.isProjMethodFinished()) && prodStatus.formFieldsActive());
        projectIdentification_ProjCategoryId.allowEdit((prodTable.isProjMethodLinkedToOrder() || prodTable.isProjMethodFinished()) && prodStatus.formFieldsActive());
        projectIdentification_ItemId.allowEdit((prodTable.isProjMethodLinkedToOrder() || prodTable.isProjMethodFinished()) && prodStatus.formFieldsActive());
        projectIdentification_ProjLinePropertyId.allowEdit((prodTable.isProjMethodLinkedToOrder() || prodTable.isProjMethodFinished()) && prodStatus.formFieldsActive());
        project_activityNumber.allowEdit(prodTable.isProjMethodFinished()|| (prodTable.isProjMethodConsumed()&& prodTable.projIsUnposted()));
        projectCostPrice.allowEdit(prodTable.isProjMethodFinished() && prodStatus.formFieldsActive());
        projectSalesPrice.allowEdit((prodTable.isProjMethodFinished() && prodStatus.formFieldsActive()));
        projectSalestax.allowEdit((prodTable.isProjMethodFinished() && prodStatus.formFieldsActive()));

        //Setting Warehouse field to read-only for 'completed' Production Orders
        inventDim_ds.object(fieldNum(InventDim,InventLocationId)).allowEdit(prodTable.ProdStatus != ProdStatus::Completed);

        prodTable_ds.object(fieldNum(ProdTable,DlvDate)).mandatory(prodTable.isProjMethodFinished());
        prodTable_ds.object(fieldNum(ProdTable,ProjId)).mandatory(prodTable.isProjMethodLinkedToOrder() || prodTable.isProjMethodFinished() || prodTable.isProjMethodConsumed());
        prodTable_ds.object(fieldNum(ProdTable,ProjCategoryId)).mandatory(prodTable.isProjMethodFinished());
        prodTable_ds.object(fieldNum(ProdTable,ProjLinePropertyId)).mandatory(prodTable.isProjMethodFinished());
        prodTable_ds.object(fieldNum(ProdTable,ProjSalesCurrencyId)).mandatory(prodTable.isProjMethodFinished());
        prodTable_ds.object(fieldNum(ProdTable,ProjSalesUnitId)).mandatory(prodTable.isProjMethodFinished());

        tab_ProjectError.visible(isQtyError);
        if (isQtyError)
        {
            tab_ProjectError.allowEdit(prodTable.ProdStatus != ProdStatus::Completed);

            prodTableProjError_ds.object(fieldNum(ProdTableProj,TransDate)).mandatory(true);
            prodTableProjError_ds.object(fieldNum(ProdTableProj,ProjCategoryId)).mandatory(true);
            prodTableProjError_ds.object(fieldNum(ProdTableProj,ProjLinePropertyId)).mandatory(true);
            prodTableProjError_ds.object(fieldNum(ProdTableProj,ProjSalesCurrencyId)).mandatory(true);
            prodTableProjError_ds.object(fieldNum(ProdTableProj,ProjSalesUnitId)).mandatory(true);
        }

        tab_ProjectOver.visible(isOverdelivery);
        if (isOverdelivery)
        {
            tab_ProjectOver.allowEdit(prodTable.ProdStatus != ProdStatus::Completed);

            prodTableProjOver_ds.object(fieldNum(ProdTableProj,TransDate)).mandatory(true);
            prodTableProjOver_ds.object(fieldNum(ProdTableProj,ProjCategoryId)).mandatory(true);
            prodTableProjOver_ds.object(fieldNum(ProdTableProj,ProjLinePropertyId)).mandatory(true);
            prodTableProjOver_ds.object(fieldNum(ProdTableProj,ProjSalesCurrencyId)).mandatory(true);
            prodTableProjOver_ds.object(fieldNum(ProdTableProj,ProjSalesUnitId)).mandatory(true);
        }

        tab_ProjectUnder.visible(isUnderconsumed);
        if (isUnderconsumed)
        {
            tab_ProjectUnder.allowEdit(prodTable.ProdStatus != ProdStatus::Completed);

            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,TransDate)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjCategoryId)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjLinePropertyId)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjSalesCurrencyId)).mandatory(true);
            prodTableProjUnder_ds.object(fieldNum(ProdTableProj,ProjSalesUnitId)).mandatory(true);
        }
    }
    else
    {
        tab_Project.visible(false);
        tab_ProjectError.visible(false);
        tab_ProjectOver.visible(false);
        tab_ProjectUnder.visible(false);
    }
}

