boolean validate()
{
    boolean ret = true;

    if (prodTable)
    {
        if (isQtyError)
            ret = prodTableProjError_ds.validateWrite();

        if (ret && isOverdelivery)
            ret = prodTableProjOver_ds.validateWrite();

        if (ret && isUnderconsumed)
            ret = prodTableProjUnder_ds.validateWrite();

        if (!ret)
            element.design().controlName(identifierStr(Overview)).setFocus();
    }

    return ret;
}

private void updateTransTaxInformation()
{
    ttsbegin;
    TransTaxInformation transTaxInformationTable = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(prodTable, true);
    transTaxInformationTable.CompanyLocation = TaxLogisticsLocationControllerHelper_IN::getDefaultLogisticsLocation(inventDim.InventLocationId, inventDim.InventSiteId);
    transTaxInformationTable.TaxInformation = TaxInformation_IN::findDefaultbyLocation(transTaxInformationTable.CompanyLocation).RecId;
    TransTaxInformationHelper::initFromTaxInformation(transTaxInformationTable);
    transTaxInformationTable.doUpdate();
    ttscommit;
}

