public void init()
{
    super();

    if (element.args().dataset() == tableNum(ProjTable))
    {
        queryBuildRangeProjId = prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable, ProjId));
    }

    this.query().literals(2);

    pmfFormCtrl_ProdTable_ProdTable.dataSourceInitPost(prodTable);
    prodTable_ds.cacheAddMethod(tableMethodStr(ProdTable, qualityOrderStatusDisplay));

    if (traceFilter)
    {
        traceFilter.parmMainDataSource(prodTable_ds);
        traceFilter.parmFilterDataSource(prodTableTmpFilter_ds);
        traceFilter.dataSourceInitPost();
    }
}

public boolean validateWrite()
{
    boolean ret;

    prodTable.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    if (inCreateFromForm)
        return true;

    ret = super();

    if (prodTable.isProjMethodConsumed())
    {
        if (ret && prodTable.prodTableProj(ProjItemTypeProdTable::ErrorQuantity))
            ret = element.projJournalFormTransError(prodTableProjError, prodTable).validateWritePost();

        if (ret && prodTable.overDelivery())
            ret = element.projJournalFormTransOver(prodTableProjOver, prodTable).validateWritePost();

        if (ret && prodTable.prodTableProj(ProjItemTypeProdTable::Underconsumed))
            ret = element.projJournalFormTransUnder(prodTableProjUnder, prodTable).validateWritePost();
    }

    return ret;
}

void write(boolean _onlySuper = false)
{
    WHSProdTable    newWHSProdTable;

    newWHSProdTable = WHSProdTable::find(prodTable.ProdId);
    if (!newWHSProdTable.RecId)
    {
        if (WHSInventEnabled::exist(prodTable.ItemId))
        {
            whsProdTable.ProdId = prodTable.ProdId;
            whsProdTable.write();
        }
    }
    else
    {
        whsProdTable.data(newWHSProdTable);
        whsProdTable.reread();
    }

    if (skipWrite)
        return;

    pmfFormCtrl_ProdTable_ProdTable.dataSourceWritePre();

    InventMovement::bufferSetRemainQty(prodTable);

    if (_onlySuper)
    {
        inCreateFromForm = true;
        super();
        inCreateFromForm = false;
    }
    else
    {
        ttsbegin;

        prodTableForm.handleProdTableWritePreSuper();

        super();

        prodTableForm.handleProdTableWritePostSuper();

        ttscommit;
    }

    prodTable.statusClient().updateWarnings();

    prodTable_ds.reread();
}

void linkActive()
{
    ProdTableReferences     prodTableReferences;
    QueryBuildRange         projIdProdTable;
    ProjTable               projTableLink;
    ProjInvoiceTable        projInvoiceTableLink;
    ProjWIPTable            projWIPTableLink;
    CostControlTransCommittedCost committedCostLink;

    switch (element.args().dataset())
    {
        case tableNum(ProjTable)        :
            projTableLink = element.args().record();
            queryBuildRangeProjId.value(projTableLink.ProjId+'*');
            break;
        case tableNum(ProjInvoiceTable):
            projIdProdTable    = prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).findRange(fieldNum(ProdTable,ProjId));

            while (projIdProdTable)
            {
                prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).clearRange(fieldNum(ProdTable,ProjId));
                projIdProdTable  = prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).findRange(fieldNum(ProdTable,ProjId));
            }

            projInvoiceTableLink   = element.args().record();

            while select ProjId from projTableLink
                where projTableLink.ProjInvoiceProjId   == projInvoiceTableLink.ProjInvoiceProjId
            {
                prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable,ProjId)).value(projTableLink.ProjId);
            }
            break;

        case tableNum(ProjWIPTable)     :
            projIdProdTable    = prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).findRange(fieldNum(ProdTable,ProjId));

            while (projIdProdTable)
            {
                prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).clearRange(fieldNum(ProdTable,ProjId));
                projIdProdTable  = prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).findRange(fieldNum(ProdTable,ProjId));
            }

            projWIPTableLink   =   element.args().record();

            while select ProjId from projTableLink
                where projTableLink.wipProject  == projWIPTableLink.ProjId
            {
                prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable,ProjId)).value(projTableLink.ProjId);
            }

            break;

        case tableNum(CostControlTransCommittedCost):
            committedCostLink   =   element.args().record();
            if (committedCostLink.CommittedCostOrig==CommittedCostOrig::Production)
                prodTable_ds.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable, ProdId)).value(committedCostLink.RefId);
            break;
    }

    if (element.args() && element.args().caller())
    {
        if (element.args().dataset() == tableNum(SalesLine))
        {
            prodTableReferences = new ProdTableReferences();
            prodTableReferences.initfromSalesLine(element.args().record());
            prodTableReferences.addQueryRanges(prodTable_ds.query());
        }
    }

    super();
}

void create(
    boolean _append         = false,
    boolean _onlySuper      = false
    )
{
    if (_onlySuper)
    {
        pmfFormCtrl_ProdTable_ProdTable.dataSourceCreatePre();

        super(_append);
    }
    else
    {
        if (prodTableForm.handleProdTableCreatePreSuper())
        {
            element.setActive();
            prodtable_ds.insertAtEnd(true);
            super(true);
            prodtable_ds.insertAtEnd(false);

            prodTable.data(createProdTable);
            inventDim.data(createInventDim);

            prodTable_ds.write(true);
            if(ProductionOrderCreationDSResearchFlight::instance().isEnabled())
            {
                prodTable_ds.research(true);
            }
        }
    }

    pmfFormCtrl_ProdTable_ProdTable.dataSourceCreatePost();
}

public int active()
{
    int ret;

    ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        isQtyError = (prodTable ? prodTable.isProjMethodConsumed() && prodTable.prodTableProj(ProjItemTypeProdTable::ErrorQuantity) : false);
        isOverdelivery = (prodTable ? prodTable.isProjMethodConsumed() && prodTable.overDelivery() : false);
        isUnderconsumed = (prodTable ? prodTable.isProjMethodConsumed() && prodTable.prodTableProj(ProjItemTypeProdTable::Underconsumed) : false);

        skipWrite = true;
        if (isQtyError)
            prodTableProjError_ds.executeQuery();
        if (isOverdelivery)
            prodTableProjOver_ds.executeQuery();
        if (isUnderconsumed)
            prodTableProjUnder_ds.executeQuery();
        skipWrite = false;
    }

    element.enableControlsActive();

    element.setmandatory();

    if (pmfFormCtrl_ProdTable_ProdTable.isFormulaEnabled())
    {
        pmfFormCtrl_ProdTable_ProdTable.dataSourceActivePost();
    }
    element.enableProjSalesCurrencyId();

    element.setGanttColor();

    return ret;
}

public void research(boolean _retainPosition = false)
{
    if (_retainPosition && InventDistinctProductExpanded_ds.queryBuildDataSource().enabled())
    {
        // Refresh loses position if one of the datasources for the root query is a view, so we need to override the default logic.
        element.args().lookupRecord(ProdTable);
        super(false);
    }
    else
    {
        super(_retainPosition);
    }
}

