public void executeQuery()
{
    QueryBuildDataSource  qbds;
    Query                 query;

    if (!prodTable.ProjId)
        return;

    query = this.query();
    qbds = query.dataSourceName(this.name());
    qbds.clearRanges();
    qbds.addRange(fieldNum(ProdTableProj, ProdId)).value(prodTable.ProdId);
    qbds.addRange(fieldNum(ProdTableProj, Type)).value(queryValue(ProjItemTypeProdTable::ErrorQuantity));

    super();
}

