void clicked()
{
    if (traceFilter)
    {
        // From tracing, the query has a join to the filter temp table but the new form will not have the same temp table instance,
        // do not copy the caller query (this limits the new form to just the currently selected record).
        this.copyCallerQuery(CopyCallerQuery::No);
    }

    super();
}

/// <summary>
/// This method is called when the Add to consolidated order is clicked
/// </summary>
void clicked()
{
    FormRun formRun;
    Args    args;

    super();
    args = new Args(formStr(PmfAddToConsOrder));
    args.caller(element);
    args.record(prodTable);
    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();
    if (formRun.closedOk())
    {
        prodTable_ds.active();
    }
}

/// <summary>
/// This method is called when the Remove from consolidated order is called
/// </summary>
/// <remarks>
/// <syntax>
/// void clicked()
/// </syntax>
/// </remarks>
void clicked()
{
    ProdTable updProdTable;

    super();

    if (Box::yesNo("@PRO2484",DialogButton::Yes) == DialogButton::Yes)
    {
        ttsbegin;
        updProdTable = ProdTable::find(prodTable.ProdId,true);
        updProdTable.PmfConsOrdId = "";
        updProdTable.update();
        ttscommit;
        prodTable_ds.active();
    }
}

