public void doReread()
{
    prodTable_DS.reread();
    prodTable_DS.refresh();
    prodTable_DS.active();
}

public void doFormCache(container _packRecs)
{
    Container mapContainer = conPeek(_packRecs, 1);
    int updatedRecordCount = 0;

    if (mapContainer != conNull())
    {
        updatedRecordCount = Map::create(mapContainer).elements();
    }

    // The ProdTable data source on a list page is of type FormDataSource instead of FormDataSource.ProdTable
    // and will cause the formCacheAll call to fail with following error :
    // "FormDataSource (object), method create called with invalid parameters."
    // Therefore we skip the form cache call and only refresh the data source on the list page.

    if (ProdTable::countCollectReferences(prodTable.ProdId) > 1 || updatedRecordCount > 1)
    {
        element.args().lookupRecord(ProdTable::find(prodTable.ProdId));
        prodTable_DS.research(false);
    }
    else
    {
        element.doReread();
    }
}
