void init()
{
    pmfFormCtrl_ProdTableOverview_ProdTable = PmfFormCtrl_ProdTableOverview_ProdTable::newFromForm(this);
    pmfFormCtrl_ProdTableOverview_ProdBOM   = PmfFormCtrl_ProdTableOverview_ProdBOM::newFromForm(this);
    pmfFormCtrl_ProdTableOverview_ProdTable.initPre();
    pmfFormCtrl_ProdTableOverview_ProdBOM.initPre();

    super();

    xSysLastValue::getLast(this);

    prodRouteTransCacheRoute        = ProdRouteTransCache::constructOpr();
    prodRouteTransCacheJob          = ProdRouteTransCache::constructJob();

    prodTableReportedFinishCache    = new Map(Types::String,Types::Real);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    pmfFormCtrl_ProdTableOverview_ProdTable.setDatasources(prodTable);
    pmfFormCtrl_ProdTableOverview_ProdBOM.setDatasources(prodBOM, prodTable);

    pmfFormCtrl_ProdTableOverview_ProdTable.initPost();
    pmfFormCtrl_ProdTableOverview_ProdBOM.initPost();

    if (ProdApplyUserTimezoneOffsetFlight::instance().isEnabled())
    {
        this.showTimeWithUserTimezone(ProdParameters::find().ShowSchedDateTimeWithUserTimeZone);
    }
}

public container pack()
{
    return [#CurrentVersion,
        ctrlLockProdPoolId.value(),
        ctrlProdPool.text(),
        ctrlLockProdType.value(),
        ctrlProdType.selection(),
        ctrlPmfProdType.selection(),
        ctrlLockBackStatus.value(),
        ctrlBackStatus.selection(),
        ctrlLockProdStatus.value(),
        ctrlProdStatus.selection()
        ];
}

