    return pmfFormCtrl_ProdTableOverview_ProdTable.isFormulaEnabled();
}

public boolean unpack(container packedClass)
{
    Integer                 version      = conpeek(packedClass,1);
    NoYes                   lockProdType;
    NoYes                   lockProdPoolId;
    NoYes                   lockBackStatus;
    ProdPoolId              prodPoolId;
    ProdType                prodType;
    PmfProdType             pmfProdType;
    ProdBackStatus          prodBackStatus;
    NoYes                   lockProdStatus;
    ProdStatus              prodStatus;

    switch (version)
    {
        case #CurrentVersion :
            [version,#CurrentList] = packedClass;
            break;
        default :
            return false;
    }

    ctrlProdType.selection(prodType);
    ctrlPmfProdType.selection(enum2int(pmfProdType));
    ctrlLockProdType.value(lockProdType);
    ctrlLockProdPoolId.value(lockProdPoolId);
    ctrlLockBackStatus.value(lockBackStatus);
    ctrlBackStatus.selection(prodBackStatus);
    ctrlProdStatus.selection(prodStatus);
    ctrlProdPool.text(prodPoolId);
    ctrlLockProdStatus.value(lockProdStatus);

    return true;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm       inventDimParm;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);

            inventDimParm.initCovDimAllGrp();
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm.data());

        case InventDimFormDesignUpdate::Active        :
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void cursorNotify(int _event)
{
    ProdTable   prodTableCache;
    Set         setProdId;
    container   conProdIdReportedFinish;

    super(_event);

    if (_event == 0 || _event == 1)
    {
        prodTableCache = prodTable_DS.getFirst(0,false);
        while (prodTableCache)
        {
            if (! prodTableReportedFinishCache.exists(prodTableCache.ProdId))
            {
                if (! setProdId)
                    setProdId = new Set(Types::String);

                setProdId.add(prodTableCache.ProdId);
            }

            prodTableCache = prodTable_DS.getNext();
        }

        if (setProdId)
        {
            conProdIdReportedFinish = ProdTableJour::reportedFinishedGoodDisplayCache(setProdId.pack(),prodTableReportedFinishCache.pack());
            if (conProdIdReportedFinish)
            {
                prodTableReportedFinishCache = Map::create(conProdIdReportedFinish);
            }
        }
    }
}

void init()
{
    super();

    pmfFormCtrl_ProdTableOverview_ProdTable.dataSourceInitPost(prodTable);

    this.query().dataSourceNo(1).addRange(fieldnum(ProdTable,ProdId));
    this.query().dataSourceNo(1).addRange(fieldnum(ProdTable,Name));
    this.query().dataSourceNo(1).addRange(fieldnum(ProdTable,ItemId));

    criteriaProdType        = this.query().dataSourceNo(1).addRange(fieldnum(ProdTable,ProdType));
    criteriaProdStatus      = this.query().dataSourceNo(1).addRange(fieldnum(ProdTable,ProdStatus));
    criteriaBackStatus      = this.query().dataSourceNo(1).addRange(fieldnum(ProdTable,BackorderStatus));
    criteriaProdPool        = this.query().dataSourceNo(1).addRange(fieldnum(ProdTable,ProdPoolId));
}

void executeQuery()
{
    if (ctrlLockProdType.value())
        criteriaProdType.value(queryValue(ctrlProdType.selection()));
    else
        criteriaProdType.value(SysQuery::valueUnlimited());

    if (ctrlLockProdPoolId.value())
        criteriaProdPool.value(queryValue(ctrlProdPool.text()));
    else
        criteriaProdPool.value(SysQuery::valueUnlimited());

    if (ctrlLockBackStatus.value())
        criteriaBackStatus.value(ctrlBackStatus.selection() ? queryValue(ctrlBackStatus.selection()) : queryValue(''));
    else
        criteriaBackStatus.value(SysQuery::valueUnlimited());

    if (ctrlLockProdStatus.value())
        criteriaProdStatus.value(queryValue(ctrlProdStatus.selection()));
    else
        criteriaProdStatus.value(SysQuery::valueUnlimited());

    pmfFormCtrl_ProdTableOverview_ProdTable.dataSourceExecuteQueryPre();

    super();
}

