Method.Source:
using Microsoft.Dynamics.Platform.Integration.Office;
[Form]
public class ProjAdvancedJournalLine extends FormRun implements OfficeIMenuCustomizer, OfficeITemplateCustomExporter
{
    ProjAdvancedJournal projAdvancedJournal;
    ProjAdvancedJournalLine lineFromArgs;
    private ProjAdvancedJournalLine originalDocument;
    boolean expenseTaxesNeedUpdating;
    private boolean projOpsEnabled;

    FormObservableLink hourTotalsObservable = new FormObservableLink();
    FormObservableLink expenseTotalsObservable = new FormObservableLink();
    FormObservableLink feeTotalsObservable = new FormObservableLink();
    FormObservableLink itemTotalsObservable = new FormObservableLink();

    private TaxWithholdParameters_IN taxWithholdParameters_IN;
    private boolean shouldUseTaxExtensionTable =
        FeatureStateProvider::isFeatureEnabled(TaxProjOpsIntegrationJournal_INFeature::instance())
        && TaxParameters::checkTaxParameters_IN();

    private boolean isProjAdvancedJournalPostingEnhancementFlightEnabled = FeatureStateProvider::isFeatureEnabled(ProjAdvancedJournalPostingEnhancementFeature::instance());
    private boolean isTaxIntegrationEnabled = Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Project);

}

public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    str templateName = resourceStr(ProjAdvancedJournalLineTemplate);
    str jourLineEntity = tableStr(ProjAdvancedJournalLineEntity);

    DocuTemplate template = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, templateName);
    Map filtersToApply = new Map(Types::String, Types::Class);

    if (template && template.TemplateID == templateName)
    {
        str jourEntity = tableStr(ProjAdvancedJournalEntity);
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(jourEntity);
        FilterBinaryNode filter = filterBuilder.areEqual(fieldStr(ProjAdvancedJournalEntity, JournalRecId), projAdvancedJournal.RecId);
        filtersToApply.insert(jourEntity, filter);
        filterBuilder = new ExportToExcelFilterTreeBuilder(jourLineEntity);
        filter = filterBuilder.areEqual(fieldStr(ProjAdvancedJournalLineEntity, ProjAdvancedJournal), projAdvancedJournal.RecId);

        if (jourLineEntity)
        {
            filtersToApply.insert(jourLineEntity, filter);
        }

    }

    return filtersToApply;
}

public void updateTemplateSettings(OfficeTemplateExportMenuItem _menuItem, Microsoft.Dynamics.Platform.Integration.Office.SettingsManager _settingsManager)
{
    // No updates to the template settings are required.
}

public void run()
{
    super();

    if (lineFromArgs)
    {
        element.setFocusToRecord(lineFromArgs);
    }
    else if (originalDocument)
    {
        element.displayOriginalDocument(originalDocument);
    }
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    // Clear the menu options which are driven by the root datasource of an entity.
    ListIterator dataEntityIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityIterator.more())
    {
        dataEntityIterator.delete();
    }

    if (ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled())
    {
        DocuTemplate docuTemplate = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, resourceStr(ProjAdvancedJournalLineTemplate));

        if (docuTemplate)
        {
            OfficeTemplateExportMenuItem menuItem = OfficeTemplateExportMenuItem::constructWithDocuTemplate(docuTemplate, docuTemplate.TemplateID);

            _menuOptions.customMenuItems().addEnd(menuItem);
        }
    }
}

public void init()
{
    projOpsEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    if (element.args() && element.args().record() && element.args().dataset() == tableNum(ProjAdvancedJournal))
    {
        projAdvancedJournal = element.args().record();
    }
    else if (element.args() && element.args().record() && element.args().dataset() == tableNum(ProjAdvancedJournalLine))
    {
        // Fetches original document reference on voucher and sets project advanced journal line
        // to display the original document line details based on the transaction type.
        originalDocument = element.args().record();
    }
    else
    {
        throw error("@SYS22996"); // Form called incorrectly
    }

    if (element.args().lookupRecord() && element.args().lookupRecord().TableId == tableNum(ProjAdvancedJournalLine))
    {
        lineFromArgs = element.args().lookupRecord();
    }

    super();

    element.initializeControls();

    if (TaxWithholdParameters_IN::checkTaxParameters())
    {
        taxWithholdParameters_IN = TaxWithholdParameters_IN::find();
    }

    if (shouldUseTaxExtensionTable)
    {
        this.createProjAdvancedJournalLineTaxExtensionINIfNotExist();
    }
}

private void createProjAdvancedJournalLineTaxExtensionINIfNotExist()
{
    if (ProjAdvancedJournal
        && !ProjAdvancedJournal.isPosted())
    {
        ProjAdvancedJournalLine projAdvancedJournalLineLocal;
        ProjAdvancedJournalLineTaxExtensionIN ProjAdvancedJournalLineTaxExtensionINLocal;
        while select * from projAdvancedJournalLineLocal
            where projAdvancedJournalLineLocal.ProjAdvancedJournal == projAdvancedJournal.RecId
                notexists join ProjAdvancedJournalLineTaxExtensionINLocal
                   where ProjAdvancedJournalLineTaxExtensionINLocal.ProjAdvancedJournalLine == projAdvancedJournalLineLocal.RecId
        {
            ProjAdvancedJournalLineTaxExtensionINLocal.ProjAdvancedJournalLine = projAdvancedJournalLineLocal.RecId;
            ProjAdvancedJournalLineTaxExtensionINLocal.initFromProjAdvancedJournalLine(projAdvancedJournalLineLocal);
            ProjAdvancedJournalLineTaxExtensionINLocal.insert();
            if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
            {
                TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(ProjAdvancedJournalLineTaxExtensionINLocal);
            }
        }
    }
}

/// <summary>
///     Shifts the focus on the form to original project advanced journal line .
/// </summary>
/// <param name="_projAdvancedJournalLine">
///     The project advanced journal line to focus on.
/// </param>
public void displayOriginalDocument(ProjAdvancedJournalLine _projAdvancedJournalLine)
{
    if (_projAdvancedJournalLine)
    {
        QueryBuildRange range;

        switch (_projAdvancedJournalLine.TransType)
        {
            case ProjTransType::Hour:
                range = SysQuery::findOrCreateRange(ProjAdvancedJournalLine_Hours_ds.queryBuildDataSource(), fieldNum(ProjAdvancedJournalLine, Voucher));
                range.value(_projAdvancedJournalLine.Voucher);
                ProjAdvancedJournalLine_Hours_ds.executeQuery();
                HourTabPage.setFocus();
                break;

            case ProjTransType::Cost:
                range = SysQuery::findOrCreateRange(ProjAdvancedJournalLine_Expense_ds.queryBuildDataSource(), fieldNum(ProjAdvancedJournalLine, Voucher));
                range.value(_projAdvancedJournalLine.Voucher);
                ProjAdvancedJournalLine_Expense_ds.executeQuery();
                ExpenseTabPage.setFocus();
                break;

            case ProjTransType::Revenue:
                range = SysQuery::findOrCreateRange(ProjAdvancedJournalLine_Fee_ds.queryBuildDataSource(), fieldNum(ProjAdvancedJournalLine, Voucher));
                range.value(_projAdvancedJournalLine.Voucher);
                ProjAdvancedJournalLine_Fee_ds.executeQuery();
                FeeTabPage.setFocus();
                break;

            case ProjTransType::Item:
                range = SysQuery::findOrCreateRange(ProjAdvancedJournalLine_Item_ds.queryBuildDataSource(), fieldNum(ProjAdvancedJournalLine, Voucher));
                range.value(_projAdvancedJournalLine.Voucher);
                ProjAdvancedJournalLine_Item_ds.executeQuery();
                ItemTabPage.setFocus();
                break;

            default:
                break;
        }
    }
}

/// <summary>
///     Shifts the focus on the form to a specific project advanced journal line.
/// </summary>
/// <param name="_projAdvancedJournalLine">
///     The project advanced journal line to focus on.
/// </param>
public void setFocusToRecord(ProjAdvancedJournalLine _projAdvancedJournalLine)
{
    if (_projAdvancedJournalLine)
    {
        switch (_projAdvancedJournalLine.TransType)
        {
            case ProjTransType::Hour:
                ProjAdvancedJournalLine_Hours_ds.positionToRecord(_projAdvancedJournalLine);
                HourTabPage.setFocus();
                break;

            case ProjTransType::Cost:
                ProjAdvancedJournalLine_Expense_ds.positionToRecord(_projAdvancedJournalLine);
                ExpenseTabPage.setFocus();
                break;

            case ProjTransType::Revenue:
                ProjAdvancedJournalLine_Fee_ds.positionToRecord(_projAdvancedJournalLine);
                FeeTabPage.setFocus();
                break;

            case ProjTransType::Item:
                ProjAdvancedJournalLine_Item_ds.positionToRecord(_projAdvancedJournalLine);
                FeeTabPage.setFocus();
                break;

            default:
                break;
        }

    }
}

/// <summary>
///     Enables and disables controls on the form based on project advanced journal and line states.
/// </summary>
public void initializeControls()
{
    boolean isPosted = projAdvancedJournal.isPosted();

    // Disable editing after record is posting (tax and offset fields are editable before posting)
    ProjAdvancedJournalLine_Hours_ds.allowEdit(!isPosted);
    ProjAdvancedJournalLine_Expense_ds.allowEdit(!isPosted);
    ProjAdvancedJournalLine_Fee_ds.allowEdit(!isPosted);
    ProjAdvancedJournalLine_Item_ds.allowEdit(!isPosted);

    // Disable deleting after lines are posted
    ProjAdvancedJournalLine_Hours_ds.allowDelete(!isPosted);
    ProjAdvancedJournalLine_Expense_ds.allowDelete(!isPosted);
    ProjAdvancedJournalLine_Fee_ds.allowDelete(!isPosted);
    ProjAdvancedJournalLine_Item_ds.allowDelete(!isPosted);

    if (isPosted)
    {
        element.viewEditModeHelper().setViewEditMode(ViewEditMode::View);
    }

    ProjLedgerTransVoucher.enabled(isPosted);
    ProjLedgerTransVoucherHours.enabled(isPosted);
    ProjLedgerTransVoucherExpense.enabled(isPosted);
    ProjLedgerTransVoucherFee.enabled(isPosted);
    ProjLedgerTransVoucherItem.enabled(isPosted);
    Post.enabled(!isPosted);

    boolean subcontractActualsPostingFeatureEnabled = FeatureStateProvider::isFeatureEnabled(ProjEnableSubcontractActualsPostingFeature::instance());
    ProjAdvancedJournalLine_Hours_SubcontractLine.visible(subcontractActualsPostingFeatureEnabled);
    ProjAdvancedJournalLine_Item_SubcontractLine.visible(subcontractActualsPostingFeatureEnabled);
    ProjAdvancedJournalLine_Expense_SubcontractLine.visible(subcontractActualsPostingFeatureEnabled);

    if (FeatureStateProvider::isFeatureEnabled(TaxProjOpsIntegrationJournal_INFeature::instance()))
    {
        if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
        {
            TaxDocumentLauncher_Expense.visible(true);
            TaxTransSource_Expense.visible(false);
            TaxInformation_Expense.visible(true);
            ProjAdvancedJournalLine_Expense_ds.object(fieldNum(ProjAdvancedJournalLine, TaxGroup)).visible(false);
            ProjAdvancedJournalLine_Expense_ds.object(fieldNum(ProjAdvancedJournalLine, TaxItemGroup)).visible(false);
            ProjAdvancedJournalLine_Expense_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxGroup)).visible(false);
            ProjAdvancedJournalLine_Expense_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxItemGroup)).visible(false);

            TaxInformation_Hour.visible(true);
            ProjAdvancedJournalLine_Hours_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxGroup)).visible(false);
            ProjAdvancedJournalLine_Hours_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxItemGroup)).visible(false);

            TaxInformation_Fee.visible(true);
            ProjAdvancedJournalLine_Fee_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxGroup)).visible(false);
            ProjAdvancedJournalLine_Fee_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxItemGroup)).visible(false);

            TaxInformation_Item.visible(true);
            ProjAdvancedJournalLine_Item_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxGroup)).visible(false);
            ProjAdvancedJournalLine_Item_ds.object(fieldNum(ProjAdvancedJournalLine, ProjectSalesTaxItemGroup)).visible(false);
        }

        if (TaxWithholdParameters_IN::checkTaxParameters())
        {
            TaxWithholdTrans_Expense.visible(true);
            this.setTaxWithholdTransExpenseButtonEnabled();
        }
    }

    // Set controls for the ProjAdvancedJournalPostingEnhancementFlight
    MarkLinesAsDraftForHour.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
    MarkLinesAsDraftForExpense.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
    MarkLinesAsDraftForFee.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
    MarkLinesAsDraftForItem.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
    ProjAdvancedJournalLine_Hours_Status.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
    ProjAdvancedJournalLine_Expense_Status.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
    ProjAdvancedJournalLine_Fee_Status.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
    ProjAdvancedJournalLine_Item_Status.visible(isProjAdvancedJournalPostingEnhancementFlightEnabled);
}

private boolean selectedRecordsCanBeMarkedAsDraft(FormDataSource _formDataSource)
{
    MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(_formDataSource);

    ProjAdvancedJournalLine projAdvancedJournalLine = multiSelectionHelper.getFirst();
    boolean linesCanBeMarkedDraft = false;

    while (projAdvancedJournalLine)
    {
        if (projAdvancedJournalLine.Status == ProjAdvancedJournalLineStatus::Processing || projAdvancedJournalLine.Status == ProjAdvancedJournalLineStatus::Error)
        {
            linesCanBeMarkedDraft = true;
            break;
        }

        projAdvancedJournalLine = multiSelectionHelper.getNext();
    }

    return linesCanBeMarkedDraft;
}

/// <summary>
///     Run the subledger journal form for the selected project advanced journal lines.
/// </summary>
/// <param name="_projAdvancedJournalLine">
///     The project advanced journal line record to show the subledger journal entries for; optional.
/// </param>
/// <remarks>
///     If no line is provided, method will open the form for all lines on the project advanced journal.
/// </remarks>
public void showAccounting(ProjAdvancedJournalLine _projAdvancedJournalLine = null)
{
    Args args = new Args();
    args.caller(element);

    SourceDocumentHeaderImplementation sourceDocumentHeaderImplementation = SourceDocumentHeader::find(projAdvancedJournal.SourceDocumentHeader).getSourceDocumentImplementation();
    args.record(sourceDocumentHeaderImplementation);

    if (_projAdvancedJournalLine)
    {
        SourceDocumentLineList sourceDocumentLineList = SourceDocumentLineList::construct();
        sourceDocumentLineList.addStart((SourceDocumentLine::find(_projAdvancedJournalLine.SourceDocumentLine)));
        args.parmObject(sourceDocumentLineList);
    }

    FormRun formRun = new MenuFunction(menuitemDisplayStr(SubledgerJournalAccountEntries), MenuItemType::Display).create(args);
    formRun.run();
}

/// <summary>
///     Computes taxes and opens the sales tax form for the project advanced journal lines selected.
/// </summary>
public void showTaxes(ProjAdvancedJournalLine _projAdvancedJournalLine)
{
    if (!_projAdvancedJournalLine.validateTaxInformation())
    {
        return;
    }

    if (element.projAdvancedJournal.isPosted())
    {
        Args args = new Args();

        args.caller(element);
        args.record(_projAdvancedJournalLine);

        FormRun formRun = new MenuFunction(menuitemDisplayStr(TaxTrans), MenuItemType::Display).create(args);
        formRun.run();
    }
    else
    {
        ProjAdvancedJournalLineTaxDocument projAdvancedJournalLineTaxDocument = ProjAdvancedJournalLineTaxDocument::construct(_projAdvancedJournalLine);
        projAdvancedJournalLineTaxDocument.calcTax();
        projAdvancedJournalLineTaxDocument.tax().sourceSingleLine(true, true);

        Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkProjAdvancedJournal), projAdvancedJournalLineTaxDocument.tax(), _projAdvancedJournalLine);
    }
}

/// <summary>
///     Recompute taxes and reset distributions if tax information has changed and
///     uncommitted tax records already exist for the provided project advanced journal line.
/// </summary>
/// <param name="_projAdvancedJournalLine">
///     The project advanced journal line record.
/// </param>
public void updateTaxInformation(ProjAdvancedJournalLine _projAdvancedJournalLine)
{
    if (TaxProjOperationUtils::recalculateTax(_projAdvancedJournalLine))
    {
        Box::infoOnce("@Proj:TaxInformationUpdated", "@Proj:TaxesRecalculatedAndDistributionsReset", "@Proj:OwnerForTaxInformationUpdatedForProjectAdvancedJournal");
    }
}

