/// <summary>
///     Jump to the required form to view more details of the record.
/// </summary>
/// <param name = "_menuItem">
///     The menu item that opens the form for more details.
/// </param>
/// <param name = "_record">
///     The record to view details for, Project, Category, Tax etc.
/// </param>
private void viewDetailsJumpRef(str _menuItem, Common _record)
{
    MenuFunction    menuFunction    = new MenuFunction(_menuItem, MenuItemType::Display);
    Args            args            = new Args();

    if (_record.TableId == tableNum(smmActivities))
    {
        args.lookupField(fieldNum(smmActivities, ActivityNumber));
    }

    args.lookupRecord(_record);
    args.caller(element);

    menuFunction.copyCallerQuery(CopyCallerQuery::No);
    menuFunction.run(args);
}

private void setProjAdvJournalLineTaxExtFieldsAllowEdit_IN(FormDataSource _fromDataSource)
{
    if (_fromDataSource.name() == formDataSourceStr(ProjAdvancedJournalLine, ProjAdvancedJournalLineTaxExtensionIN_Expense)
        && FeatureStateProvider::isFeatureEnabled(TaxProjOpsIntegrationJournal_INFeature::instance()))
    {
        boolean shouldProcessTaxes = ProjAdvancedJournalPost::shouldProcessTaxes(ProjAdvancedJournalLine_Expense);

        // Cost assessable value isn't allowed edit for cost price if expense inits from pending vendor invoice. 
        ProjAdvancedJournalLineTaxExtensionIN_Expense_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, AssessableValueTransactionCurrency)).allowEdit(shouldProcessTaxes);

        // Withholding tax group isn't allowed edit for cost price exists only(sales price is 0) if expense inits from pending vendor invoice.
        boolean isWHTTaxGroupAllowEdit = taxWithholdParameters_IN 
            && !(ProjAdvancedJournalLine_Expense.ExtendedCost && !ProjAdvancedJournalLine_Expense.ExtendedPrice && !shouldProcessTaxes);
        ProjAdvancedJournalLineTaxExtensionIN_Expense_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TDSGroup)).allowEdit(isWHTTaxGroupAllowEdit);
        ProjAdvancedJournalLineTaxExtensionIN_Expense_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TCSGroup)).allowEdit(isWHTTaxGroupAllowEdit);
    }
}

private void setTaxWithholdEnabled_IN(FormDataSource _fromDataSource)
{
    if (taxWithholdParameters_IN)
    {
        boolean isTDSActive = taxWithholdParameters_IN.TDSActivate;
        boolean isTCSActive = taxWithholdParameters_IN.TCSActivate;

        switch (_fromDataSource.name())
        {
            case formDataSourceStr(ProjAdvancedJournalLine, ProjAdvancedJournalLineTaxExtensionIN_Hours) :
                ProjAdvancedJournalLineTaxExtensionIN_Hours_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TDSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Hours.TCSGroup && isTDSActive);
                ProjAdvancedJournalLineTaxExtensionIN_Hours_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TCSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Hours.TDSGroup && isTCSActive);
                break;

            case formDataSourceStr(ProjAdvancedJournalLine, ProjAdvancedJournalLineTaxExtensionIN_Expense) :
                ProjAdvancedJournalLineTaxExtensionIN_Expense_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TDSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Expense.TCSGroup && isTDSActive);
                ProjAdvancedJournalLineTaxExtensionIN_Expense_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TCSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Expense.TDSGroup && isTCSActive);

                this.setTaxWithholdTransExpenseButtonEnabled();
                break;

            case formDataSourceStr(ProjAdvancedJournalLine, ProjAdvancedJournalLineTaxExtensionIN_Fee) :
                ProjAdvancedJournalLineTaxExtensionIN_Fee_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TDSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Fee.TCSGroup && isTDSActive);
                ProjAdvancedJournalLineTaxExtensionIN_Fee_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TCSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Fee.TDSGroup && isTCSActive);
                break;

            case formDataSourceStr(ProjAdvancedJournalLine, ProjAdvancedJournalLineTaxExtensionIN_Item) :
                ProjAdvancedJournalLineTaxExtensionIN_Item_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TDSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Item.TCSGroup && isTDSActive);
                ProjAdvancedJournalLineTaxExtensionIN_Item_ds.object(fieldNum(ProjAdvancedJournalLineTaxExtensionIN, TCSGroup))
                    .enabled(!ProjAdvancedJournalLineTaxExtensionIN_Item.TDSGroup && isTCSActive);
                break;

            default :
                break;
        }
    }
}

private void setTaxWithholdTransExpenseButtonEnabled()
{
    TaxWithholdTrans_Expense.enabled(
        ProjAdvancedJournalLine_Expense.ExtendedCost
        && !ProjAdvancedJournalLine_Expense.isPosted()
        && ProjAdvancedJournalLine_Expense.OffsetAccountType == LedgerJournalACType::Vend
        && ((ProjAdvancedJournalLineTaxExtensionIN_Expense.TDSGroup && taxWithholdParameters_IN.TDSActivate)
            || (ProjAdvancedJournalLineTaxExtensionIN_Expense.TCSGroup && taxWithholdParameters_IN.TCSActivate))
        && ProjAdvancedJournalPost::shouldProcessTaxes(ProjAdvancedJournalLine_Expense));
}

private void markLinesAsDraft(MultiSelectionHelper _multiSelectionHelper)
{    
    ProjAdvancedJournalLine projAdvancedJournalLine = _multiSelectionHelper.getFirst();

    while (projAdvancedJournalLine)
    {
        if (projAdvancedJournalLine.Status == ProjAdvancedJournalLineStatus::Error
                || projAdvancedJournalLine.Status == ProjAdvancedJournalLineStatus::Processing)
        {
            projAdvancedJournalLine.updateJournalLineStatusToDraft();
        }

        projAdvancedJournalLine = _multiSelectionHelper.getNext();
    }
}

private boolean anyMarkedLineProcessing(MultiSelectionHelper _multiSelectionHelper)
{
    ProjAdvancedJournalLine projAdvancedJournalLine = _multiSelectionHelper.getFirst();
    boolean processingLines = false;

    while (projAdvancedJournalLine)
    {
        if (projAdvancedJournalLine.Status == ProjAdvancedJournalLineStatus::Processing)
        {
            processingLines = true;
            break;
        }

        projAdvancedJournalLine = _multiSelectionHelper.getNext();
    }

    return processingLines;
}

private void updateToDraftButtonClick(FormDataSource _formDataSource)
{
    MultiSelectionHelper multiSelectionHelper = MultiSelectionHelper::construct();
    multiSelectionHelper.parmDatasource(_formDataSource);

    boolean markedLines = element.anyMarkedLineProcessing(multiSelectionHelper);

    if (!markedLines
            || (markedLines && Box::okCancel("@Proj:AdvancedJournalLineMoveToDraftBoxMessage", DialogButton::Cancel) == DialogButton::Ok))
    {
        element.markLinesAsDraft(multiSelectionHelper);
    }

    _formDataSource.research(true);
}

public void init()
{
    super();

    // Add range for transaction type
    QueryBuildRange typeRange = SysQuery::findOrCreateRange(ProjAdvancedJournalLine_Hours_ds.queryBuildDataSource(), fieldnum(ProjAdvancedJournalLine, TransType));
    typeRange.value(queryValue(ProjTransType::Hour));
    typeRange.status(RangeStatus::Hidden);
    typeRange.status(RangeStatus::Locked);
}

public int active()
{
    int ret = super();

    SubledgerJournalAccountEntries.enabled(true);
    ViewDistributions.enabled(true);

    MarkLinesAsDraftForHour.enabled(element.selectedRecordsCanBeMarkedAsDraft(ProjAdvancedJournalLine_Hours_ds));

    return ret;
}

public void markChanged()
{
    super();

    MarkLinesAsDraftForHour.enabled(element.selectedRecordsCanBeMarkedAsDraft(ProjAdvancedJournalLine_Hours_ds));
}

public void init()
{
    super();

    // Add range for transaction type
    QueryBuildRange typeRange = SysQuery::findOrCreateRange(ProjAdvancedJournalLine_Expense_ds.queryBuildDataSource(), fieldnum(ProjAdvancedJournalLine, TransType));
    typeRange.value(queryValue(ProjTransType::Cost));
    typeRange.status(RangeStatus::Hidden);
    typeRange.status(RangeStatus::Locked);
}

public void write()
{
    super();

    if (expenseTaxesNeedUpdating && !isTaxIntegrationEnabled)
    {
        element.updateTaxInformation(ProjAdvancedJournalLine_Expense);
        expenseTaxesNeedUpdating = false;
        ProjAdvancedJournalLine_Expense_ds.reread();
    }
}

public int active()
{
    int ret = super();

    if (FeatureStateProvider::isFeatureEnabled(TaxProjOpsIntegrationJournal_INFeature::instance())
        && TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        if (ProjAdvancedJournalLine_Expense.canCalculateGST())
        {
            TaxDocumentLauncher_Expense.enabled(true);
        }
        else
        {
            TaxDocumentLauncher_Expense.enabled(false);
        }
    }

    MarkLinesAsDraftForExpense.enabled(element.selectedRecordsCanBeMarkedAsDraft(ProjAdvancedJournalLine_Expense_ds));

    return ret;
}

public void markChanged()
{
    super();

    MarkLinesAsDraftForExpense.enabled(element.selectedRecordsCanBeMarkedAsDraft(ProjAdvancedJournalLine_Expense_ds));
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ProjAdvancedJournalLine_Expense.OffsetAccountType != LedgerJournalACType::Ledger
        && ProjAdvancedJournalLine_Expense.OffsetAccountType != LedgerJournalACType::Cust
        && ProjAdvancedJournalLine_Expense.OffsetAccountType != LedgerJournalACType::Vend
        && ProjAdvancedJournalLine_Expense.OffsetAccountType != LedgerJournalACType::Bank)
    {
        ret = ret && checkFailed(strFmt("@Proj:OffsetAccountTypeInvalidForProjAdvancedJournalLineError", ProjAdvancedJournalLine_Expense.OffsetAccountType, ProjAdvancedJournalLine_Expense.Voucher));
    }

    return ret;
}

