public void clicked()
{
    boolean ret = true;

    ProjAdvancedJournalLine projAdvancedJournalLine;

    while select * from projAdvancedJournalLine
        where projAdvancedJournalLine.ProjAdvancedJournal == projAdvancedJournal.RecId
    {
        if (!projAdvancedJournalLine.validateTaxInformation())
        {
            ret = false;
        }
    }

    if (ret)
    {
        Args args = new Args();
        args.caller(element);
        args.menuItemName(menuitemDisplayStr(AccountingDistributionsDocumentView));
        args.menuItemType(MenuItemType::Display);
        args.record(projAdvancedJournal);

        MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(AccountingDistributionsDocumentView), MenuItemType::Display);
        menuFunction.run(args);
    }
}

