public void clicked()
{
    if (!ProjAdvancedJournalLine_Expense.isPosted())
    {
        TaxWithholdCalcProjectAdvancedJournal_IN::calculateWithholdTaxForJournal(projAdvancedJournal.RecId);

        ProjAdvancedJournalLineTaxWithholdDocument_IN projAdvancedJournalLineTaxWithholdDoc = ProjAdvancedJournalLineTaxWithholdDocument_IN::construct(ProjAdvancedJournalLine_Expense);
        projAdvancedJournalLineTaxWithholdDoc.calcTax();

        TaxWithhold_IN::showWithholdTaxes(projAdvancedJournalLineTaxWithholdDoc.taxWithhold(), ProjAdvancedJournalLine_Expense);
    }
    else
    {
        super();
    }
}

