    boolean             cdsLockingOptionOnAccountTrans;

    private boolean enableProjMultipleContractLinesForProjectFeature = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();

}

private void deleteSelectedRecords(FormDataSource _formDataSource)
{
    if (_formDataSource.cursor().RecId == 0)
    {
        _formDataSource.delete();
    }
    else if (Box::confirm('@SYS100114'))
    {
        MultiSelectionHelper selectionHelper = MultiSelectionHelper::construct();
        selectionHelper.parmDatasource(_formDataSource);
        Common selectedRecord;
        Common deletingRecord;

        selectedRecord = selectionHelper.getFirst();

        ttsbegin;

        while (selectedRecord)
        {
            switch(selectedRecord.TableId)
            {
                case tableNum(ProjBegBalJournalTrans_CostSales):
                    deletingRecord = ProjBegBalJournalTrans_CostSales::find(selectedRecord.RecId, true);
                    break;

                case tableNum(ProjBegBalJournalTrans_Fee):
                    deletingRecord = ProjBegBalJournalTrans_Fee::find(selectedRecord.RecId, true);                        
                    break;

                case tableNum(ProjBegBalJournalTrans_OnAcc):
                    deletingRecord = ProjBegBalJournalTrans_OnAcc::find(selectedRecord.RecId, true);                        
                    break;
            }

            if (deletingRecord.RecId != 0)
            {
                deletingRecord.delete();
            }

            selectedRecord = selectionHelper.getNext();
        }

        ttscommit;

        _formDataSource.research();
    }
}

public void activate(boolean _active)
{
    super(_active);

    journalFormTrans.formInitPost();
}

public void close()
{
    // Call below to JournalFormTrans.formInitPost can
    // sometimes try to close the form again, creating a loop.
    if (closing)
    {
        return;
    }
    closing = true;

    journalFormTrans.formInitPost();
    journalFormTrans.formClosePre();

    super();
}

public void enableDisableAccruable()
{
    boolean costSalesEnable = true;
    boolean feeEnable       = true;

    if (projBegBalJournalTrans_CostSales.ProjId)
    {
        ProjTable projTableCostSales = ProjTable::find(projBegBalJournalTrans_CostSales.ProjId);
        costSalesEnable = costSalesEnable && ProjMultipleContractLinesForProjectFeatureHelper::isTimeMaterialBillingMethod(projTableCostSales, ProjBegBalJournalTrans_CostSales::begBalTransType2ProjTransType(projBegBalJournalTrans_CostSales.ProjTransType));
    }

    if (projBegBalJournalTrans_CostSales.LinePropertyId)
    {
        costSalesEnable = costSalesEnable && ProjLineProperty::find(projBegBalJournalTrans_CostSales.LinePropertyId).ToBeAccrued;
    }

    if (projBegBalJournalTrans_CostSales.ProjFundingSource)
    {
        costSalesEnable = costSalesEnable && ProjFundingSource::isExternalFunding(projBegBalJournalTrans_CostSales.ProjFundingSource);
    }

    costSalesEnable = costSalesEnable && projBegBalJournalTrans_CostSales.ProjTransStatusPostedInvoiced == ProjTransStatusPostedInvoiced::Posted;

    if (projBegBalJournalTrans_Fee.ProjId)
    {
        ProjTable projTableFee = ProjTable::find(projBegBalJournalTrans_Fee.ProjId);
        feeEnable = feeEnable && ProjMultipleContractLinesForProjectFeatureHelper::isTimeMaterialBillingMethod(projTableFee, ProjTransType::Revenue);
    }

    if (projBegBalJournalTrans_Fee.LinePropertyId)
    {
        feeEnable = feeEnable && ProjLineProperty::find(projBegBalJournalTrans_Fee.LinePropertyId).ToBeAccrued;
    }

    if (projBegBalJournalTrans_Fee.ProjFundingSource)
    {
        feeEnable = feeEnable && ProjFundingSource::isExternalFunding(projBegBalJournalTrans_Fee.ProjFundingSource);
    }

    feeEnable = feeEnable && projBegBalJournalTrans_Fee.ProjTransStatusPostedInvoiced == ProjTransStatusPostedInvoiced::Posted;

    if (!costSalesEnable)
    {
        projBegBalJournalTrans_CostSales.ProjAccruable = false;
    }

    if (!feeEnable)
    {
        projBegBalJournalTrans_Fee.ProjAccruable = false;
    }

    costSales_ProjAccruable.allowEdit(costSalesEnable);
    fee_ProjAccruable.allowEdit(feeEnable);
}

