/// <summary>
///    Sets control properties depending on the transaction type.
/// </summary>
/// <param name="_projTransType">
///    A beginning balance transaction type associated with the selected record.
/// </param>
void updateCostSalesGrid(BegBalProjTransTypeCost _projTransType = BegBalProjTransTypeCost::None)
{
    switch (_projTransType)
    {
        case BegBalProjTransTypeCost::Cost:
            overview_Txt.lookupButton(0);
            overview_CostPrice.allowEdit(true);
            overview_SalesPrice.allowEdit(true);
            overview_ItemId.allowEdit(false);
            ResourceCostSales.allowEdit(true);
            ResourceCategoryCostSales.allowEdit(false);
            break;

        case BegBalProjTransTypeCost::Hour:
            overview_Txt.lookupButton(1);
            overview_CostPrice.allowEdit(true);
            overview_SalesPrice.allowEdit(true);
            overview_ItemId.allowEdit(false);
            ResourceCostSales.allowEdit(true);
            ResourceCategoryCostSales.allowEdit(true);
            break;

        case BegBalProjTransTypeCost::Item:
            overview_Txt.lookupButton(1);
            overview_CostPrice.allowEdit(true);
            overview_SalesPrice.allowEdit(true);
            overview_CostAmount.allowEdit(true);
            overview_ItemId.allowEdit(true);
            ResourceCostSales.allowEdit(false);
            ResourceCategoryCostSales.allowEdit(false);
            break;

        default:
            projBegBalJournalTrans_CostSales_ds.allowEdit(!projJournalTable.Posted);
            projBegBalJournalTrans_CostSales_ds.allowDelete(!projJournalTable.Posted);
            projBegBalJournalTrans_CostSales_ds.allowCreate(!projJournalTable.Posted);

            projBegBalJournalTrans_Fee_ds.allowEdit(!projJournalTable.Posted);
            projBegBalJournalTrans_Fee_ds.allowDelete(!projJournalTable.Posted);
            projBegBalJournalTrans_Fee_ds.allowCreate(!projJournalTable.Posted);

            projBegBalJournalTrans_OnAcc_ds.allowEdit(!projJournalTable.Posted);
            projBegBalJournalTrans_OnAcc_ds.allowDelete(!projJournalTable.Posted);
            projBegBalJournalTrans_OnAcc_ds.allowCreate(!projJournalTable.Posted);
            break;
    }
}

