/// <summary>
///    Initializes cost price, sales price and default category when the field value is modified.
/// </summary>
public void modified()
{
    super();

    projBegBalJournalTrans_CostSales.initValue();
    if (projBegBalJournalTrans_CostSales.ProjTransType != BegBalProjTransTypeCost::Item)
    {
        projBegBalJournalTrans_CostSales.Resource = projJournalTable.Resource;
        projBegBalJournalTrans_CostSales.setResourceCategory();
    }
    projBegBalJournalTrans_CostSales.Txt = projJournalTable.Txt;
    projBegBalJournalTrans_CostSales.initDefaultCategory(projJournalTable);
    projBegBalJournalTrans_CostSales_ds.updateCostSalesGrid(projBegBalJournalTrans_CostSales.ProjTransType);

    if (projBegBalJournalTrans_CostSales.ProjTransType != BegBalProjTransTypeCost::Hour && projBegBalJournalTrans_CostSales.ProjTransType != BegBalProjTransTypeCost::None &&
    ProjTable::find(projBegBalJournalTrans_CostSales.ProjId).Type == ProjType::Time)
    {
        checkFailed(strFmt("@SYS136760", projBegBalJournalTrans_CostSales.ProjTransType, "@SYS8758"));
    }

    projBegBalJournalTrans_CostSales.setLineProperty();
    if (projBegBalJournalTrans_CostSales.ProjTransType == BegBalProjTransTypeCost::Hour)
    {
        projBegBalJournalTrans_CostSales.setHourCostPrice();
        projBegBalJournalTrans_CostSales.setHourSalesPrice();
    }

    if (projBegBalJournalTrans_CostSales.ProjTransType == BegBalProjTransTypeCost::Cost)
    {
        projBegBalJournalTrans_CostSales.setExpenseCostPrice();
        projBegBalJournalTrans_CostSales.setExpenseSalesPrice();
    }

    projBegBalJournalTrans_CostSales_ds.rereadReferenceDataSources();
}

