public boolean validate()
{
    boolean ret = super();

    ProjTable projTableLocal = ProjTable::find(projBegBalJournalTrans_OnAcc.ProjId);

    if (!(projTableLocal.Type == ProjType::FixedPrice ||
          projTableLocal.Type == ProjType::TimeMaterial ||
          (element.enableProjMultipleContractLinesForProjectFeature && projTableLocal.ProjInvoiceProjId)))
    {
        ret = checkFailed(strFmt("@SYS135537", "@SYS7659"));
    }

    return ret;
}

