public void lookup()
{
    Args   args = new Args();

    args.name(formStr(ProjTableLookup));
    args.caller(this);
    args.record(projBegBalJournalTrans_Fee);
    args.lookupValue(projBegBalJournalTrans_Fee.ProjId);

    Object formRun = classfactory.formRunClass(args);            
    if (!element.enableProjMultipleContractLinesForProjectFeature)
    {
        formRun.byProjType(ProjType::TimeMaterial);
    }
    formRun.init();

    this.performFormLookup(formRun);
}

