public class ProjBudget extends FormRun implements IProjIdSelectable, IProjBudgetGridUpdatable
{
    ProjTable               projTable;
    ProjId                  projId;
    RecId                   projBudgetRecId;
    SysLastValue            sysLastValue;
    ProjBaseBudgetOn        budgetSourceOn;
    ForecastModelId         sourceForecastModelID;
    ProjId                  selectedProjId;
    ProjId                  sourceProjectTransactionID;
    ProjBudget              projBudgetLocal;
    ProjTree                projTree;
    boolean                 expandRevenueTab;
    boolean                 callFromWorkflow;
    ProjTable               selectedProjTable;

    private boolean enableProjMultipleContractLinesForProjectFeature = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();

    #define.Star("*")
    #define.CurrentVersion(1)

    #localmacro.CurrentList
            BudgetSourceOn,
            sourceForecastModelID
    #endmacro
}

public Set allowedBudgetSourceSet()
{
    Set allowedBudgetSourceSet;

    allowedBudgetSourceSet = new Set(Types::Enum);

    return allowedBudgetSourceSet;
}

public void allowRevenueLineCreation()
{
    selectedProjTable = ProjTable::find(selectedProjId);

    if (selectedProjTable.Type == ProjType::TimeMaterial ||
        selectedProjTable.Type == ProjType::FixedPrice ||
        (element.enableProjMultipleContractLinesForProjectFeature && selectedProjTable.ProjInvoiceProjId))
    {
        revenue.visible(true);
        if (projBudget.allowEditBudgetLineGrid())
        {
            generateFromCostBudgetLines.enabled(true);
        }
    }
    else
    {
        revenue.visible(false);
    }
}

public void budgetQuery()
{
    QueryBuildDataSource projBudgetDS;

    projBudgetRecId = projBudgetLocal.RecId;

    if (callFromWorkflow && projBudgetRecId == 0)
    {
        return;
    }

    projBudgetDS = projBudget_ds.query().dataSourceTable(tableNum(ProjBudget));
    projBudgetDS.clearDynalinks();
    projBudgetDS.clearRange(fieldNum(ProjBudget, RecId));
    projBudgetDS.addRange(fieldNum(ProjBudget, RecId)).value(queryValue(projBudgetRecId));
}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = projBudget.canSubmitToWorkflow();

    return ret;
}

public void updateWorkflowControls()
{
    super();

    if (ProjParameters::find().DisplayWorkflowInstructions)
    {
        ProjBudgetLine projBudgetLine;

        select firstonly RecId from projBudgetLine
            where projBudgetLine.ProjBudget == projBudget.RecId;

        if (projBudgetLine.RecId)
        {
            ProjBudgetOrigWorkflowDropDialog.enabled(true);
        }
        else
        {
            ProjBudgetOrigWorkflowDropDialog.enabled(false);
        }
    }
    else
    {
        ProjBudgetOrigWorkflowDropDialog.visible(false);
    }
}

