/// <summary>
/// Retrieves the sum of cost budget lines and update the summary control on the form.
/// </summary>
public void getSummaryofCostBudgetLine()
{
    ProjBudgetLine  localProjectBudgetLocal;

    localProjectBudgetLocal = projBudgetLineCost.calcTotalBudgetLine(selectedProjId);

    originalBudgetTotal.realValue(localProjectBudgetLocal.OriginalBudget);
    committedRevisionsTotal.realValue(localProjectBudgetLocal.CommittedRevisions);
    uncommittedRevisionsTotal.realValue(localProjectBudgetLocal.UncommittedRevisions);
    budgetLineTotal.realValue(localProjectBudgetLocal.TotalBudget);
}

/// <summary>
/// Retrieves the sum of revenue budget line and update the summary control on the form.
/// </summary>
public void getSummaryofRevenueBudgetLine()
{
    ProjBudgetLine  localProjectBudgetLocal;

    localProjectBudgetLocal = projBudgetLineRevenue.calcTotalBudgetLine(selectedProjId);

    revenueOriginalBudgetTotal.realValue(localProjectBudgetLocal.OriginalBudget);
    revenueCommittedRevisionsTotal.realValue(localProjectBudgetLocal.CommittedRevisions);
    revenueUncommittedRevisionsTotal.realValue(localProjectBudgetLocal.UncommittedRevisions);
    revenueBudgetAmountTotal.realValue(localProjectBudgetLocal.TotalBudget);
}

/// <summary>
/// Retrieves the value form controls from the <c>xSysLastValue</c>.
/// </summary>
public void init()
{
    super();

    xSysLastValue::getLast(this);

    element.updateWorkflowControls();

    element.setBudgetBasisGroupControl();
    element.updateLoadButton();

    if (element.args() &&
       element.args().menuItemType() == MenuItemType::Display &&
       element.args().menuItemName() == menuitemDisplayStr(ProjBudget))
    {
        callFromWorkflow = true;
    }

    // Called from UWL
    if (element.args().lookupRecord())
    {
        if (element.args().dataset() == tableNum(ProjBudget))
        {
            projBudgetLocal = element.args().lookupRecord() as ProjBudget;
            projTable = ProjTable::find(projBudgetLocal.RootProjId);
            projId = projBudgetLocal.RootProjId;
        }
    }
    else if (element.args().record())
    {
        if (element.args().dataset() == tableNum(ProjTable))
        {
            projTable = element.args().record() as ProjTable;
            //creates or finds budget for selected project if independent budgeting is effective.
            //Otherwise, creates or finds budget for root proj of the selected proj.
            if (projTable.ProjBudgetManagement == ProjBudgetManagement::Independent && projTable.UseBudgeting)
            {
                projId = projTable.ProjId;
            }
            else
            {
                projId = ProjTable::getRootProjId(projTable.ProjId);
            }
            projBudgetLocal = ProjBudget::findOrCreateProjectBudget(projId);
        }
        else if (element.args().dataset() == tableNum(ProjBudget))
        {
            projBudgetLocal = element.args().record() as ProjBudget;
            projTable = ProjTable::find(projBudgetLocal.RootProjId);
            projId = ProjTable::getRootProjId(projBudgetLocal.RootProjId);
        }
    }
    else if (!callFromWorkflow)
    {
        throw error ("@SYS22996");
    }

    this.initInternal();
}

/// <summary>
/// This method is to initialize the form control.Add this method is for workflow scenario.
/// </summary>
/// <remarks>
/// The method is called by init and findRecord.
/// </remarks>
public void initInternal()
{
    projectTitleText.text(ProjBudgetSource::getProjBudgetTitleText(projTable, projBudgetLocal.RecId));

    element.budgetQuery();

    selectedProjId = projBudgetLocal.RootProjId;

    expandRevenueTab = false;

    // Is the selected project part of a hierarchy? and independent budgeting is not effective
    if (selectedProjId && (ProjTable::child(selectedProjId) || projTable.ParentId != '') && !(projTable.UseBudgeting && projTable.ProjBudgetManagement == ProjBudgetManagement::Independent))
    {
        projTree = ProjTree::construct(tree, ProjTreeType::ProjectHierarchy, projBudgetLocal.RootProjId);
        projTree.parmCreateMaster(true);
        projTree.setupChart();
        projTree.selectNode(projTable.ProjId);

        treeGroup.visible(true);
        splitterGroup.visible(true);
    }

    this.allowRevenueLineCreation();
}

