/// <summary>
/// Determines whether category and transaction controls are enabled or disabled.
/// </summary>
/// <param name="_projBudgetLine">
/// A <c>ProjBudgetLine</c> table buffer that contains the project budget line.
/// </param>
/// <returns>
/// true if the criteria meet to enable the record, otherwise, false.
/// </returns>
public boolean isNewProjBudgetLine(ProjBudgetLine _projBudgetLine)
{
    boolean isNewBudgetLine;

    isNewBudgetLine = true;

    if ((projBudget.BudgetState ==  ProjBudgetState::Amendment && _projBudgetLine.RecId &&  (_projBudgetLine.OriginalBudget != 0 || _projBudgetLine.CommittedRevisions != 0) )
        || projBudget.BudgetState ==  ProjBudgetState::Committed
        )
    {
        isNewBudgetLine = false;
    }

    return isNewBudgetLine;
}

