/// <summary>
/// Forces a refresh on the budget lines.
/// </summary>
/// <param name="_budgetLineRecId">
/// The current budget line's recid.
/// </param>
public void refreshBudgetLine(RefRecId _budgetLineRecId)
{
    if (projBudgetLineCost.RecId == _budgetLineRecId)
    {
        projBudgetLineCost_DS.reread();
        projBudgetLineCost_DS.refresh();
        projBudgetLineCost_DS.selectionChanged();
        element.getSummaryofCostBudgetLine();
    }

    if (projBudgetLineRevenue.RecId == _budgetLineRecId)
    {
        projBudgetLineRevenue_DS.reread();
        projBudgetLineRevenue_DS.refresh();
        projBudgetLineRevenue_DS.selectionChanged();
        element.getSummaryofRevenueBudgetLine();
    }
}

