public void setForecastButtonAccess()
{
    ProjStatusType projStatusType = projTable.status();

    if (projStatusType.ctrlForecast())
    {
        itemForecast.enabled(projStatusType.ctrlForecastItem());
        expenseForecast.enabled(projStatusType.ctrlForecastCost());
        feeForecast.enabled(projStatusType.ctrlForecastRevenue());
        hourForecast.enabled(projStatusType.ctrlForecastHour());
    }
    else
    {
        itemForecast.enabled(false);
        expenseForecast.enabled(false);
        feeForecast.enabled(false);
        hourForecast.enabled(false);
    }
}

public boolean unpack(container packedClass)
{
    Integer     version     = conPeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            break;

        default:
            return false;
    }

    return true;
}

/// <summary>
/// Updates the design of <c>ProjectBudget</c> form according to the value of <c>ProjBudgetState</c> field in the <c>ProjBudget</c> table.
/// </summary>
public void updateDesign()
{
    element.setForecastButtonAccess();

    createAllocateButton.enabled(projBudget.allowEditBudgetLineGrid());
    importFromForecastOrProject.enabled(projBudget.allowEditBudgetLineGrid());
    editCostUncommitted.enabled(projBudget.allowEditAllocationButton());
    editRevenueUncommitted.enabled(projBudget.allowEditAllocationButton());
    viewCommitted.enabled(projBudget.allowViewAllocationButton());

    newRevision.enabled(projBudget.allowNewRevisionCreation());
    editRevision.enabled(projBudget.allowEditRevisionButton());

    projBudgetLineRevenue_ds.allowCreate(projBudget.allowEditBudgetLineGrid());
    projBudgetLineRevenue_ds.allowDelete(projBudget.allowEditBudgetLineGrid());
    projBudgetLineRevenue_ds.allowEdit(projBudget.allowEditBudgetLineGrid());

    projBudgetLineCost_ds.allowCreate(projBudget.allowEditBudgetLineGrid());
    projBudgetLineCost_ds.allowDelete(projBudget.allowEditBudgetLineGrid());
    projBudgetLineCost_ds.allowEdit(projBudget.allowEditBudgetLineGrid());

    budgetHeaderGroup.enabled(projBudget.allowEditBudgetForecastSelection());

    projBudgetLineTable_OriginalBudgetCost.enabled(projBudget.allowEditFieldOriginalAmount());
    projBudgetLineTable_OriginalBudgetCost.skip(!projBudget.allowEditFieldOriginalAmount());

    projBudgetLineTable_RevenueOriginalBudget.enabled(projBudget.allowEditFieldOriginalAmount());
    projBudgetLineTable_RevenueOriginalBudget.skip(!projBudget.allowEditFieldOriginalAmount());

    projBudget_ds.allowEdit(projBudget.allowEditBudgetLineGrid());

    element.updateGridActionButtons();
}

private void updateGridActionButtons()
{
    boolean allow;

    allow = projBudget.allowEditBudgetLineGrid();

    costNew.enabled(allow);
    costButtonDelete.enabled(allow);
    revenueNew.enabled(allow);
    revenueButtonDelete.enabled(allow);
    generateFromCostBudgetLines.enabled(allow);
}

