/// <summary>
/// Initializes the data of ProjBudget form from ProjBudget table.
/// </summary>
/// <param name = "_recordContext"> The record context </param>
/// <returns> returns true if recordContext exists </returns>
public boolean applyRecordContext(str _recordContext)
{
    boolean ret;

    ret = super(_recordContext);

    if (ret && callFromWorkflow && ProjBudget)
    {
        projBudgetLocal.data(ProjBudget);
        projBudgetRecId = ProjBudget.RecId;
        selectedProjId = ProjBudget.RootProjId;
        projectTitleText.text(ProjBudgetSource::getProjBudgetTitleText(ProjTable::find(selectedProjId), projBudgetRecId));
    }

    return ret;
}

/// <summary>
/// Provide the query to select <c>ProjBudgetLine</c> of specific project id, category id, line type, project budget.
/// </summary>
/// <returns>
/// Returns query instance for <c>ProjBudgetLine</c> of specific project id, category id, line type, project budget.
/// </returns>
protected Query buildProjBudgetLineQuery()
{
    Query query = new Query();
    QueryBuildDataSource projBudgetLineDS = query.addDataSource(tableNum(ProjBudgetLine));
    QueryBuildRange projBudgetRecordId = projBudgetLineDS.addRange(fieldNum(ProjBudgetLine, ProjBudget));
    QueryBuildRange projBudgetCategoryID = projBudgetLineDS.addRange(fieldNum(ProjBudgetLine, CategoryId));
    QueryBuildRange projBudgetProjId = projBudgetLineDS.addRange(fieldNum(ProjBudgetLine, ProjId));
    projBudgetRecordId.value(queryValue(projBudgetLocal.RecId));
    projBudgetCategoryID.value(SysQuery::valueNot(''));

    if (selectedProjId != '')
    {
        projBudgetProjId.value(queryValue(selectedProjId) + #Star);
    }
    else
    {
        projBudgetProjId.value(queryValue(projId) + #Star);
    }

    return query;
}

private void checkLineDetail()
{
    ProjBudgetLineDetail    projBudgetLineDetail;

    projBudgetLineRevenue_ds.object(fieldNum(ProjBudgetLine, OriginalBudget)).enabled(true);

    if (projBudgetLineRevenue.RecId)
    {
        select firstonly RecId from projBudgetLineDetail
            where projBudgetLineDetail.ProjBudgetLine == projBudgetLineRevenue.RecId &&
                projBudgetLineDetail.ProjBudgetRevisionLine == 0;

        if (projBudgetLineDetail.RecId)
        {
            projBudgetLineRevenue_ds.object(fieldNum(ProjBudgetLine, OriginalBudget)).enabled(false);
        }
    }
}

public void selectionChanged()
{
    super();
    this.checkLineDetail();
}

