/// <summary>
/// Initializes default value of the <c>ProjBudgetLine</c> table for revenue budget line.
/// </summary>
public void initValue()
{
    super();

    projBudgetLineRevenue.ProjId = selectedProjId;
    projBudgetLineRevenue.ProjBudget = projBudgetRecId;

    if (selectedProjTable.Type == ProjType::TimeMaterial ||
        selectedProjTable.Type == ProjType::FixedPrice ||
        element.enableProjMultipleContractLinesForProjectFeature)
    {
        projBudgetLineRevenue.ProjTransType = ProjTransType::Hour;
    }

    projBudgetLineRevenue.ProjBudgetLineType = ProjBudgetLineType::Revenue;
}

