public void modified()
{
    ProjBudgetLine  projBudgetLineRevenue_Orig = projBudgetLineRevenue.orig();
    ProjTable       currentProjTable = ProjTable::find(projBudgetLineRevenue.ProjId);
    if (projBudgetLineRevenue_Orig.ProjId != projBudgetLineRevenue.ProjId)
    {
        projBudgetLineRevenue.ActivityNumber = '';
    }

    super();

    if (ProjMultipleContractLinesForProjectFeatureHelper::isTimeMaterialBillingMethod(currentProjTable, projBudgetLineRevenue.ProjTransType) ||
        ProjMultipleContractLinesForProjectFeatureHelper::isFixedPriceBillingMethod(currentProjTable, projBudgetLineRevenue.ProjTransType))
    {
        projBudgetLineTable_RevenueTransactionType.allowEdit(true);
    }
    else
    {
        projBudgetLineRevenue.ProjId = projBudgetLineRevenue_Orig.ProjId;
    }
}

public void lookup(FormControl _formControl, str _filterStr)
{
    switch (projTable.ProjBudgetManagement)
    {
        case ProjBudgetManagement::Independent:
            ProjTable::lookupSelectedProjId(_formControl, _filterStr, selectedProjId);
            break;
        case ProjBudgetManagement::None:
        default:
            ProjTable::lookupProjIdHierarchy(_formControl, _filterStr, selectedProjId);
    }
}

