void clicked()
{
    FormRun         createAllocation;
    Args            allocationArgs;

    super();

    allocationArgs = new Args(formStr(ProjBudgetDimensionAllocate));
    allocationArgs.caller(element);

    allocationArgs.record(projBudget);

    createAllocation = classfactory.formRunClass(allocationArgs);

    createAllocation.run();
    createAllocation.wait();

    if (createAllocation.closedOk())
    {
        if (projBudgetLineRevenue_ds.cursor())
        {
            projBudgetLineRevenue_ds.reread();
            projBudgetLineRevenue_ds.refresh();
        }

        if (projBudgetLineCost_ds.cursor())
        {
            projBudgetLineCost_ds.reread();
            projBudgetLineCost_ds.refresh();
        }

        element.updateDesign();
    }
}

public boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    return projTree.expanding(_idx, _action, _data);
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    if (_oldItem.data())
    {
        selectedProjId = projTree.getSelection();
        projBudgetLineCost_ds.limitToSelection();
        projBudgetLineRevenue_ds.limitToSelection();

        element.allowRevenueLineCreation();

        projectTitleText.text(ProjBudgetSource::getProjBudgetTitleText(selectedProjTable, projBudgetLocal.RecId));
    }
}

