/// <summary>
/// Sets allow edit property on the allocation amount field
/// </summary>
public void allowEditAllocationLines()
{
    projBudgetRevLineAllocation_ds.allowEdit(isEditMode && !allProjects.value() && !allActivities.value() && !allCategories.value());
}

/// <summary>
/// load the transction types based on <c>projBudgetLineType</c>.
/// </summary>
/// <param name="projBudgetLineType">
/// indicate the type of transaction.
/// </param>
public void allowedTrasactionsTypes(ProjBudgetLineType projBudgetLineType)
{
    Set                 allowedTrasactionsTypes;
    SysDictEnum         sysDictEnum;
    SetEnumerator       allowedEnumValueEnumerator;
    int                 allowedEnumValue,comboBoxItemNumber;

    allowedTrasactionsTypes = new Set(Types::Enum);

    if (ProjBudgetLineType::Cost == projBudgetLineType)
    {
        allowedTrasactionsTypes.add(ProjBudgetTransType::HourCosts);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ExpenseCosts);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ItemCosts);
    }
    else if (ProjBudgetLineType::Revenue == projBudgetLineType)
    {
        allowedTrasactionsTypes.add(ProjBudgetTransType::HourRevenues);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ExpenseRevenues);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ItemRevenues);
        allowedTrasactionsTypes.add(ProjBudgetTransType::Fees);
    }
    else
    {
        allowedTrasactionsTypes.add(ProjBudgetTransType::HourCosts);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ExpenseCosts);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ItemCosts);
        allowedTrasactionsTypes.add(ProjBudgetTransType::HourRevenues);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ExpenseRevenues);
        allowedTrasactionsTypes.add(ProjBudgetTransType::ItemRevenues);
        allowedTrasactionsTypes.add(ProjBudgetTransType::Fees);
    }
    sysDictEnum = new SysDictEnum(enumNum(ProjBudgetTransType));
    transactionType.label(sysDictEnum.label());
    transactionType.helpText(sysDictEnum.help());

    allowedEnumValueEnumerator = allowedTrasactionsTypes.getEnumerator();
    transactionType.clear();

    comboBoxItemNumber = 0;
    while (allowedEnumValueEnumerator.moveNext())

    {
        allowedEnumValue = allowedEnumValueEnumerator.current();
        transactionType.item(comboBoxItemNumber);
        transactionType.add(sysDictEnum.value2Name(allowedEnumValue));
        enumValue2ComboBoxItemMap.insert(comboBoxItemNumber,allowedEnumValue);
        comboBoxItemNumber++;
    }
}

/// <summary>
/// Method is used to display the total budget revision amount
/// </summary>
/// <returns>
/// Total allocated amount
/// </returns>
/// <remarks>
/// Method calculates the total allocated budget revision for the whole period based on the category, project and activity filtering
/// </remarks>
display AmountCur calculateBudgetTotals()
{
    ProjBudgetRevLineAllocation lineSummary;
    AmountCur                   ret;

    for (lineSummary = projBudgetRevLineAllocationSummary_ds.getFirst();
         lineSummary;
         lineSummary = projBudgetRevLineAllocationSummary_ds.getNext())
    {
        ret += lineSummary.RevisionAllocationAmount;
    }

    return ret;
}

/// <summary>
/// Validates the amount left to allocate is not zero
/// </summary>
/// <returns>
/// true if the amount to allocate is not zero; otherwise, false
/// </returns>
public boolean canClose()
{
    boolean ret;

    ret = super();

    ret = ret & this.validateTotalAllocatedAmount();

    return ret;
}

/// <summary>
/// Saves value of form controls in the <c>xSysLastValue</c> before closing the form.
/// </summary>
public void close()
{
    TmpProjBudgetRevisionLine       localTmpProjBudgetRevisionLine;

    super();

    if (element.args().dataset() == tableNum(TmpProjBudgetRevisionLine))
    {
        localTmpProjBudgetRevisionLine = element.args().record();
        projBudgetRevision = ProjBudgetRevision::find(localTmpProjBudgetRevisionLine.ProjBudgetRevision);

        ttsBegin;
        ProjBudgetRevisionManager::updateUncommitedRevisions(projBudgetRevision.RecId);
        ttsCommit;
    }

    xSysLastValue::saveLast(this);
}

/// <summary>
/// Creates the query for <c>ProjBudgetRevLineAllocation</c> datasource.
/// </summary>
/// <returns>
/// A <c>Query</c> type object.
/// </returns>
public Query createQuery()
{
    Query                   query;
    ProjBudgetLine          projBudgetLineLocal;
    ProjBudgetRevisionLine  projBudgetRevisionLineLocal;
    QueryBuildDataSource    projBudgetAllocationLineDataSource;
    QueryBuildDataSource    projBudgetRevLineAllocationDataSource;
    QueryBuildDataSource    projBudgetLineDataSource;
    QueryBuildDataSource    projBudgetRevisionLineDataSource;
    QueryBuildDataSource    projBudgetRevisionDataSource;
    QueryBuildDataSource    fiscalCalendarPeriodDataSource;
    RecId                   fiscalCalendar;
    RecId                   fiscalCalendarYearRecID;
    ProjBudgetLineType      projBudgetLineType;
    ProjTransType           projTransType;
    QueryBuildDataSource    qbds;

    this.getControlValue();
    projTransType = ProjBudgetLine::getProjTransType(transactionTypes);
    projBudgetLineType = ProjBudgetLine::convertProjTransType2ProjBudgetLineType(transactionTypes);

    select RecId
        from projBudgetRevisionLineLocal
            where projBudgetRevisionLineLocal.ProjBudgetRevision == projBudgetRevision.RecId
        join RecId, ProjAllocationMethod
            from projBudgetLineLocal
                where projBudgetRevisionLineLocal.ProjBudgetLine == projBudgetLineLocal.RecId
                    && projBudgetLineLocal.ProjTransType == projTransType
                    && projBudgetLineLocal.ProjBudgetLineType == projBudgetLineType
                    && (allCategories.checked() || projBudgetLineLocal.CategoryId == categoryId.valueStr())
                    && (allProjects.checked() || projBudgetLineLocal.ProjId == projectId.valueStr())
                    && (allActivities.checked() || projBudgetLineLocal.ActivityNumber == activityNumber.valueStr());

    fiscalCalendar = Ledger::fiscalCalendar(CompanyInfo::current());
    fiscalCalendarYearRecID = FiscalCalendarYear::findByCalendarYearName(fiscalCalendar, projYearsToDisplay).RecId;

    query = new Query();
    projBudgetRevLineAllocationDataSource = query.addDataSource(tableNum(ProjBudgetRevLineAllocation));

    projBudgetAllocationLineDataSource = projBudgetRevLineAllocationDataSource.addDataSource(tableNum(ProjBudgetAllocationLine));
    projBudgetAllocationLineDataSource.joinMode(JoinMode::InnerJoin);
    projBudgetAllocationLineDataSource.fetchMode(QueryFetchMode::One2One);
    projBudgetAllocationLineDataSource.addLink(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetAllocationLine), fieldNum(ProjBudgetAllocationLine, RecId));

    if (projBudgetLineLocal.ProjAllocationMethod == ProjAllocationMethod::Periods)
    {
        fiscalCalendarPeriodDataSource  = projBudgetAllocationLineDataSource.addDataSource(tableNum(FiscalCalendarPeriod));
        fiscalCalendarPeriodDataSource.joinMode(JoinMode::InnerJoin);
        fiscalCalendarPeriodDataSource.fetchMode(QueryFetchMode::One2One);
        fiscalCalendarPeriodDataSource.addLink(fieldNum(ProjBudgetAllocationLine, FiscalCalendarPeriod), fieldNum(FiscalCalendarPeriod, RecId));
        fiscalCalendarPeriodDataSource.addRange(fieldNum(FiscalCalendarPeriod, FiscalCalendarYear)).value(queryValue(fiscalCalendarYearRecID));
    }
    else
    {
        projBudgetAllocationLineDataSource.addRange(fieldNum(ProjBudgetAllocationLine, FiscalCalendarYear)).value(queryValue(fiscalCalendarYearRecID));
    }

    if ((allCategories.checked() || allProjects.checked() || allActivities.checked()))
    {
        projBudgetRevLineAllocationDataSource.addSelectionField(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetAllocationLine), SelectionField::Min);

        projBudgetAllocationLineDataSource.addGroupByField(fieldNum(ProjBudgetAllocationLine, FiscalCalendarPeriod));
        projBudgetAllocationLineDataSource.addGroupByField(fieldNum(ProjBudgetAllocationLine, FiscalCalendarYear));
        projBudgetAllocationLineDataSource.addGroupByField(fieldNum(ProjBudgetAllocationLine, Quarter));

        projBudgetRevLineAllocationDataSource.addSelectionField(fieldNum(ProjBudgetRevLineAllocation, RevisionAllocationAmount), SelectionField::Sum);

        // Now adding the ProjBudgetLine data source
        projBudgetLineDataSource = projBudgetAllocationLineDataSource.addDataSource(tableNum(ProjBudgetLine));
        projBudgetLineDataSource.joinMode(JoinMode::ExistsJoin);
        projBudgetLineDataSource.fetchMode(QueryFetchMode::One2One);
        projBudgetLineDataSource.addLink(fieldNum(ProjBudgetAllocationLine, ProjBudgetLine),fieldNum(ProjBudgetLine, RecId));
        projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine,ProjTransType)).value(queryValue(projTransType));
        projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine,ProjBudgetLineType)).value(queryValue(projBudgetLineType));
        if (!allCategories.checked())
        {
            projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, CategoryId)).value(queryValue(categoryId.valueStr()));
        }
        if (!allProjects.checked())
        {
            projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, ProjId)).value(queryValue(projectId.valueStr()));
        }
        if (!allActivities.checked())
        {
            projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, ActivityNumber)).value(queryValue(activityNumber.valueStr()));
        }
        // Adding ProjBudgetRevisionLine data source
        projBudgetRevisionLineDataSource = projBudgetRevLineAllocationDataSource.addDataSource(tableNum(ProjBudgetRevisionLine));
        projBudgetRevisionLineDataSource.joinMode(JoinMode::ExistsJoin);
        projBudgetRevisionLineDataSource.fetchMode(QueryFetchMode::One2One);
        projBudgetRevisionLineDataSource.addLink(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetRevisionLine), fieldNum(ProjBudgetRevisionLine, RecId));

        // Adding ProjBudgetRevision data source
        projBudgetRevisionDataSource = projBudgetRevisionLineDataSource.addDataSource(tableNum(ProjBudgetRevision));
        projBudgetRevisionDataSource.joinMode(JoinMode::ExistsJoin);
        projBudgetRevisionDataSource.fetchMode(QueryFetchMode::One2One);
        projBudgetRevisionDataSource.addLink(fieldNum(ProjBudgetRevisionLine, ProjBudgetRevision), fieldNum(ProjBudgetRevision, RecId));
        projBudgetRevisionDataSource.addRange(fieldNum(ProjBudgetRevision, RecId)).value(queryValue(projBudgetRevision.RecId));
    }
    else
    {
        projBudgetRevLineAllocationDataSource.addSelectionField(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetAllocationLine));
        projBudgetRevLineAllocationDataSource.addSelectionField(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetRevisionLine));
        projBudgetRevLineAllocationDataSource.addSelectionField(fieldNum(ProjBudgetRevLineAllocation, RevisionAllocationAmount));

        projBudgetRevLineAllocationDataSource.addRange(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetRevisionLine)).value(queryValue(projBudgetRevisionLineLocal.RecId));
    }

    if (projBudgetLineLocal.ProjAllocationMethod == ProjAllocationMethod::Periods)
    {
        qbds = query.dataSourceTable(tableNum(FiscalCalendarPeriod));
        if (qbds)
        {
            qbds.addOrderByField(fieldNum(FiscalCalendarPeriod, StartDate),SortOrder::Ascending);
        }
    }

    return query;
}

public Query createQuerySummary()
{
    Query                   query;
    ProjBudgetLine          projBudgetLineLocal;
    ProjBudgetRevisionLine  projBudgetRevisionLineLocal;
    QueryBuildDataSource    projBudgetAllocationLineDataSource;
    QueryBuildDataSource    projBudgetRevLineAllocationDataSource;
    QueryBuildDataSource    projBudgetLineDataSource;
    QueryBuildDataSource    projBudgetRevisionLineDataSource;
    QueryBuildDataSource    projBudgetRevisionDataSource;
    QueryBuildDataSource    fiscalCalendarPeriodDataSource;
    ProjBudgetLineType      projBudgetLineType;
    ProjTransType           projTransType;
    QueryBuildDataSource    qbds;

    transactionTypes = enumValue2ComboBoxItemMap.lookup(transactionType.selection());
    projTransType = ProjBudgetLine::getProjTransType(transactionTypes);
    projBudgetLineType = ProjBudgetLine::convertProjTransType2ProjBudgetLineType(transactionTypes);

    select RecId
        from projBudgetRevisionLineLocal
            where projBudgetRevisionLineLocal.ProjBudgetRevision == projBudgetRevision.RecId
        join RecId, ProjAllocationMethod
            from projBudgetLineLocal
                where projBudgetRevisionLineLocal.ProjBudgetLine == projBudgetLineLocal.RecId
                    && projBudgetLineLocal.ProjTransType == projTransType
                    && projBudgetLineLocal.ProjBudgetLineType == projBudgetLineType
                    && (allCategories.checked() || projBudgetLineLocal.CategoryId == categoryId.valueStr())
                    && (allProjects.checked() || projBudgetLineLocal.ProjId == projectId.valueStr())
                    && (allActivities.checked() || projBudgetLineLocal.ActivityNumber == activityNumber.valueStr());

    query = new Query();
    projBudgetRevLineAllocationDataSource = query.addDataSource(tableNum(ProjBudgetRevLineAllocation));

    projBudgetAllocationLineDataSource = projBudgetRevLineAllocationDataSource.addDataSource(tableNum(ProjBudgetAllocationLine));
    projBudgetAllocationLineDataSource.joinMode(JoinMode::InnerJoin);
    projBudgetAllocationLineDataSource.fetchMode(QueryFetchMode::One2One);
    projBudgetAllocationLineDataSource.addLink(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetAllocationLine), fieldNum(ProjBudgetAllocationLine, RecId));

    if (projBudgetLineLocal.ProjAllocationMethod == ProjAllocationMethod::Periods)
    {
        fiscalCalendarPeriodDataSource  = projBudgetAllocationLineDataSource.addDataSource(tableNum(FiscalCalendarPeriod));
        fiscalCalendarPeriodDataSource.joinMode(JoinMode::InnerJoin);
        fiscalCalendarPeriodDataSource.fetchMode(QueryFetchMode::One2One);
        fiscalCalendarPeriodDataSource.addLink(fieldNum(ProjBudgetAllocationLine, FiscalCalendarPeriod),fieldNum(FiscalCalendarPeriod, RecId));
        fiscalCalendarPeriodDataSource.addGroupByField(fieldNum(FiscalCalendarPeriod, FiscalCalendarYear));
        fiscalCalendarPeriodDataSource.addSelectionField(fieldNum(FiscalCalendarPeriod, FiscalCalendarYear));
    }
    else
    {
        projBudgetAllocationLineDataSource.addGroupByField(fieldNum(ProjBudgetAllocationLine, FiscalCalendarYear));
        projBudgetAllocationLineDataSource.addSelectionField(fieldNum(ProjBudgetAllocationLine, FiscalCalendarYear));
    }
    projBudgetRevLineAllocationDataSource.addSelectionField(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetAllocationLine), SelectionField::Min);

    if ((allCategories.checked() || allProjects.checked() || allActivities.checked()))
    {
        // Now adding the ProjBudgetLine data source
        projBudgetLineDataSource = projBudgetAllocationLineDataSource.addDataSource(tableNum(ProjBudgetLine));
        projBudgetLineDataSource.joinMode(JoinMode::ExistsJoin);
        projBudgetLineDataSource.fetchMode(QueryFetchMode::One2One);
        projBudgetLineDataSource.addLink(fieldNum(ProjBudgetAllocationLine, ProjBudgetLine),fieldNum(ProjBudgetLine, RecId));
        projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, ProjTransType)).value(queryValue(projTransType));
        projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, ProjBudgetLineType)).value(queryValue(projBudgetLineType));
        if (!allCategories.checked())
        {
            projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, CategoryId)).value(queryValue(categoryId.valueStr()));
        }
        if (!allProjects.checked())
        {
            projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, ProjId)).value(queryValue(projectId.valueStr()));
        }
        if (!allActivities.checked())
        {
            projBudgetLineDataSource.addRange(fieldNum(ProjBudgetLine, ActivityNumber)).value(queryValue(activityNumber.valueStr()));
        }
        // Adding ProjBudgetRevisionLine data source
        projBudgetRevisionLineDataSource = projBudgetRevLineAllocationDataSource.addDataSource(tableNum(ProjBudgetRevisionLine));
        projBudgetRevisionLineDataSource.joinMode(JoinMode::ExistsJoin);
        projBudgetRevisionLineDataSource.fetchMode(QueryFetchMode::One2One);
        projBudgetRevisionLineDataSource.addLink(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetRevisionLine), fieldNum(ProjBudgetRevisionLine, RecId));

        // Adding ProjBudgetRevision data source
        projBudgetRevisionDataSource = projBudgetRevisionLineDataSource.addDataSource(tableNum(ProjBudgetRevision));
        projBudgetRevisionDataSource.joinMode(JoinMode::ExistsJoin);
        projBudgetRevisionDataSource.fetchMode(QueryFetchMode::One2One);
        projBudgetRevisionDataSource.addLink(fieldNum(ProjBudgetRevisionLine, ProjBudgetRevision), fieldNum(ProjBudgetRevision, RecId));
        projBudgetRevisionDataSource.addRange(fieldNum(ProjBudgetRevision, RecId)).value(queryValue(projBudgetRevision.RecId));
    }
    else
    {
        projBudgetRevLineAllocationDataSource.addRange(fieldNum(ProjBudgetRevLineAllocation, ProjBudgetRevisionLine)).value(queryValue(projBudgetRevisionLineLocal.RecId));
    }

    projBudgetRevLineAllocationDataSource.addSelectionField(fieldNum(ProjBudgetRevLineAllocation, RevisionAllocationAmount), SelectionField::Sum);

    if (projBudgetLineLocal.ProjAllocationMethod == ProjAllocationMethod::Periods)
    {
        qbds = query.dataSourceTable(tableNum(FiscalCalendarPeriod));
        if (qbds)
        {
            qbds.addOrderByField(fieldNum(FiscalCalendarPeriod,StartDate),SortOrder::Ascending);
        }
    }

    return query;
}

/// <summary>
/// Set the value of project budget allocation group controls.
/// </summary>
public void getControlValue()
{
    transactionTypes   = enumValue2ComboBoxItemMap.lookup(transactionType.selection());
    projYearsToDisplay = projBudgetRevLineAllocationSummary_ds.getAllocationYearName(projBudgetRevLineAllocationSummary);
}

/// <summary>
/// get the combo box index of selected transaction type.
/// </summary>
/// <returns>
/// index of the combo box.
/// </returns>
public int getMapKey()
{
    int key;
    MapEnumerator er;

    er = enumValue2ComboBoxItemMap.getEnumerator();
    while (er.moveNext())
    {
        if ( er.currentValue() == transactionTypes)
        {
            key = er.currentKey();
            break;
        }
    }
    return key;
}

/// <summary>
/// Calculates the amount left to allocate
/// </summary>
/// <param name="_differenceAmount">
/// Difference between the amount from the allocation and the new amount entered.
/// This parameter is not zero only when an allocation amount is manually modified
/// </param>
/// <returns>
/// Amount left to allocate for the current selection
/// </returns>
real getToAllocateAmount(AmountCur _differenceAmount = 0)
{
    real                        amount;
    real                        allocatedAmount;
    real                        budgetRevisionAmount;
    ProjBudgetRevisionLine      projBudgetRevisionLineLocal;
    ProjBudgetRevLineAllocation projBudgetRevLineAllocationLocal;
    boolean                     currentSelectionHaveAllocation;

    if (allCategories.checked() || allProjects.checked() || allActivities.checked())
    {
    // If allocations are summarized To Allocate amount should be 0
        return 0;
    }
    else
    {
        projBudgetRevLineAllocationLocal = ProjBudgetRevLineAllocation::find(projBudgetRevLineAllocation.RecId);

        if (projBudgetRevLineAllocationLocal)
        {
            select RecId, RevisionAmount from projBudgetRevisionLineLocal
            where projBudgetRevisionLineLocal.RecId == projBudgetRevLineAllocationLocal.ProjBudgetRevisionLine;

            currentSelectionHaveAllocation = true;
        }
    }

    if (currentSelectionHaveAllocation && projBudgetRevisionLineLocal.RecId)
    {
        allocatedAmount = ProjBudgetRevLineAllocation::getAllocatedTotals(projBudgetRevisionLineLocal.RecId);
        budgetRevisionAmount = projBudgetRevisionLineLocal.RevisionAmount;

        amount = budgetrevisionAmount - allocatedAmount + _differenceAmount;
    }
    return amount;
}

/// <summary>
/// Retrieves the value form controls from the <c>xSysLastValue</c>.
/// </summary>
public void init()
{
    ProjBudgetLine                  projBudgetLine;
    ProjBudgetRevisionLine          projBudgetRevisionLine;
    ProjBudgetRevLineAllocation     localProjBudgetRevLineAllocation;
    ProjBudgetAllocationActionType  projBudgetAllocationActionType;
    TmpProjBudgetRevisionLine       localTmpProjBudgetRevisionLine;

    super();

    enumValue2ComboBoxItemMap = new Map(Types::Integer, Types::Integer);
    if (element.args().dataset() == tableNum(TmpProjBudgetRevisionLine))
    {
        localTmpProjBudgetRevisionLine = element.args().record();
        this.allowedTrasactionsTypes(localTmpProjBudgetRevisionLine.ProjBudgetLineType);

        projBudgetLine = ProjBudgetLine::find(localTmpProjBudgetRevisionLine.ProjBudgetLine);
        projBudgetRevision = ProjBudgetRevision::find(localTmpProjBudgetRevisionLine.ProjBudgetRevision);

        projectId.text(projBudgetLine.ProjId);
        activityNumber.text(projBudgetLine.ActivityNumber);
    }
    else
    {
        projBudgetRevision = element.args().record();

        select firstonly projBudgetLine
            exists join projBudgetRevisionLine
            where projBudgetRevisionLine.ProjBudgetLine == projBudgetLine.RecId &&
                  projBudgetRevisionLine.ProjBudgetRevision == projBudgetRevision.RecId
                exists join localProjBudgetRevLineAllocation
                where localProjBudgetRevLineAllocation.ProjBudgetRevisionLine == projBudgetRevisionLine.RecId;

        this.allowedTrasactionsTypes(2);

        projectId.text(ProjBudget::find(projBudgetRevision.ProjBudget).RootProjId);
        activityNumber.text('');

        allCategories.value(1);
        allActivities.enabled(false);
        allActivities.value(1);
        allProjects.value(1);
    }

    projBudgetAllocationActionType = element.args().parmEnum();

    if (projBudgetAllocationActionType == ProjBudgetAllocationActionType::Editable && projBudgetRevision.RevisionWorkflowStatus == ProjBudgetRevisionWFStatus::Submitted)
    {
        isEditMode = false;
    }
    else if (projBudgetAllocationActionType == ProjBudgetAllocationActionType::Editable)
    {
        isEditMode = true;
    }

    xSysLastValue::getLast(this);

    categoryId.text(projBudgetLine.CategoryId);

    transactionTypes = ProjBudgetLine::convertProjTransType2ProjBudgetTransType(projBudgetLine.ProjTransType,projBudgetLine.ProjBudgetLineType);

    this.setControlValue();

    this.updateFields();

    element.updateAllCategories();
    element.updateAllActivities();
    element.updateAllProjects();
}

