    ret = super();

    if (ret)
    {
        if (ret)
        {
            projBudgetRevLineAllocation_ds.executeQuery();
            projBudgetRevLineAllocationSummary_ds.executeQuery();
        }
    }
    else
    {
        element.setControlValue();
    }
    return ret;
}

