/// <summary>
/// Filters the look up of activity number on the basis of selected project id.
/// </summary>
public void lookup()
{
    if (projectId.valueStr() == '')
    {
        throw warning("@SYS327809");
    }

    HierarchyTreeTable::lookupHierarchyTree(this, ProjTable::find(projectId.valueStr()));
}

