public class ProjCategoryGroup extends FormRun
{
}

public void AddRangeToLedgerDimensionAccountQuery(QueryBuildDataSource qbds,ProjAccountType paType)
{
    QueryBuildRange qbr;

    // There is a relation between ProjPosting and ProjCategoryGroup (one to many in that direction). Add
    // filters over the rest of the fields forming the primary key on ProjPosting to make the join 1:1.

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjFundingSource));
    qbr.value(SysQuery::value(0));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjAccountType));
    qbr.value(SysQuery::value(paType));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjCode));
    qbr.value(SysQuery::value(TableGroupAll::All));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjRelation));
    qbr.value(SysQuery::valueEmptyString());
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm*/

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, CategoryCode));
    qbr.value(SysQuery::value(TableGroupAll::GroupId));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm

    qbr = SysQuery::findOrCreateRange(qbds, fieldNum(ProjPosting, ProjCategoryRelation));
    qbr.value(SysQuery::value(projCategoryGroup.CategoryGroupId));
    qbr.status(1); // Lock the range so that it can't be edited in the SysQueryForm
}

public void init()
{
    super();

    ProjPosting_CancelInvoiceTurnover_LedgerDimension.visible(ProjParameters::find().EnableCancelPostingProfiles);
}

public void ModifiedLedgerDimension(FormDataSource projPostingds)
{
    ProjPosting projPosting;

    // Clear out the value on the buffer so a write isn't triggered.
    projPosting = projPostingds.cursor();
    projPosting.LedgerDimension = 0;
}

void setButtonAccess()
{
    projCostControl.enabled(false);
    projUtilization.enabled(false);
    switch (projCategoryGroup.CategoryType)
    {
        case ProjCategoryType::Hour :
            projCostControl.enabled(true);
            projUtilization.enabled(true);
            break;

        case ProjCategoryType::Cost :
            break;

        case ProjCategoryType::Revenue :
            break;

        case ProjCategoryType::Item :
            projCostControl.enabled(true);
            break;

        default:
            break;
    }
}

public void write()
{
    super();

    projCategoryGroup.costAccount(true, projPosting_CostAccount.LedgerDimension);
    projCategoryGroup.payrollAllocation(true, projPosting_PayrollAllocation.LedgerDimension);
    projCategoryGroup.wipCostvalue(true, projPosting_WIPCostValue.LedgerDimension);
    projCategoryGroup.offsetAccountItem(true, projPosting_OffsetAccountItem.LedgerDimension);
    projCategoryGroup.statusAccountItem(true, projPosting_StatusAccountItem.LedgerDimension);
    projCategoryGroup.accruedCost(true, projPosting_AccruedCost.LedgerDimension);
    projCategoryGroup.wipCostAccrued(true, projPosting_WIPCostAccrued.LedgerDimension);
    projCategoryGroup.invoiceTurnover(true, projPosting_InvoiceTurnOver.LedgerDimension);
    projCategoryGroup.accruedTurnoversalesValue(true, projPosting_AccruedTurnoverSalesValue.LedgerDimension);
    projCategoryGroup.wipSalesvalue(true, projPosting_WIPSalesValue.LedgerDimension);
    projCategoryGroup.accruedTurnoverProd(true, projPosting_AccruedTurnoverProd.LedgerDimension);
    projCategoryGroup.wipProduction(true, projPosting_WIPProduction.LedgerDimension);
    projCategoryGroup.accruedTurnoverProfit(true, projPosting_AccruedTurnoverProfit.LedgerDimension);
    projCategoryGroup.wipProfit(true, projPosting_WIPProfit.LedgerDimension);
    projCategoryGroup.smaAccruedRevenueSubscription(true, projPosting_SMAAccruedRevenueSub.LedgerDimension);
    projCategoryGroup.smawipSubscription(true, projPosting_SMAWIPSubscription.LedgerDimension);

    // <GBR>
    projCategoryGroup.canceledInvoiceRevenue(true, ProjPosting_InvoiceCancelRevenueAccount.LedgerDimension);
    // </GBR>
}

int active()
{
    int ret;

    ret = super();

    element.setButtonAccess();

    projPosting_CostAccount_ds.executeQuery();
    projPosting_PayrollAllocation_ds.executeQuery();
    projPosting_WIPCostValue_ds.executeQuery();
    projPosting_OffsetAccountItem_ds.executeQuery();
    projPosting_StatusAccountItem_ds.executeQuery();
    projPosting_AccruedCost_ds.executeQuery();
    projPosting_WIPCostAccrued_ds.executeQuery();

    projPosting_InvoiceTurnOver_ds.executeQuery();
    projPosting_accruedTurnoverSalesValue_ds.executeQuery();
    projPosting_WIPSalesValue_ds.executeQuery();
    projPosting_accruedTurnoverProd_ds.executeQuery();
    projPosting_WIPProduction_ds.executeQuery();
    projposting_accruedTurnoverProfit_ds.executeQuery();
    projPosting_WIPProfit_ds.executeQuery();
    projPosting_SMAAccruedRevenueSub_ds.executeQuery();
    projPosting_SMAWIPSubscription_ds.executeQuery();

    // <GBR>
    ProjPosting_InvoiceCancelRevenueAccount_ds.executeQuery();
    // </GBR>

    return ret;
}

