public void jumpRef()
{
    new MenuFunction(menuitemDisplayStr(ProjLineProperty), MenuItemType::Display).run();            
}

public boolean modified()
{
    boolean                 ret;

    if ((ctrlProjLinePropertyId.text() && ! ProjLineProperty::checkExist(ctrlProjLinePropertyId.text())))
    {
        ctrlProjLinePropertyId.text('');
    }

    ret = super();

    return ret;
}

public boolean modified()
{
    boolean ret = true;

    if (!ProjPosting_CostAccount_LedgerDimension.getValue())
    {
        element.ModifiedLedgerDimension(projPosting_CostAccount_DS);
    }
    else
    {
        ret = super();
    }

    projCategoryGroup_ds.forceWrite(true);

    return ret;
}

