Method.Source:
    [Form]
    public class ProjControlPeriodCostGroup extends FormRun
    {
        ProjControlPeriodTable  projControlPeriod;
        FormRun                 callerForm;
        ProjIControlPeriodTable formProjControlPeriodTable;
        boolean                 checkPeriod;
        private boolean         enableMultipleContractLinesForProject = ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled();
}

void bookRefresh()
{
    projControlPeriodCostGroup_DS.research();

    formProjControlPeriodTable = callerForm as ProjIControlPeriodTable;

    if (formProjControlPeriodTable != null)
    {
        formProjControlPeriodTable.bookRefresh();
    }
}

boolean checkPeriod(boolean _checkPeriod   = checkPeriod)
{
    checkPeriod = _checkPeriod;

    return checkPeriod;
}

void enableFields()
{
    if (projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::RemainingEstimate ||//Remaining budget
        projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::TotBudgetMinusActual ||//Total budget - Actual
        projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::WorkProgressPercentage ||//Percentage complete based on budget and actual
        projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::TotalEstimateActual)//Set cost to complete to zero
    {
        ctrlTotalAmount.allowEdit(false);
        ctrlTotalQty.allowEdit(false);
        ctrlTotalAmountTab.allowEdit(false);
        ctrlTotalQtyTab.allowEdit(false);
    }
    else
    {
        ctrltotalAmount.allowEdit(true);
        ctrlTotalQty.allowEdit(true);
        ctrltotalAmountTab.allowEdit(true);
        ctrlTotalQtyTab.allowEdit(true);
    }
}

public void init()
{
    callerForm = element.args().caller();

    super();

    if (enableMultipleContractLinesForProject)
    {
        CtrlCommittedMenu.enabled(false);
        CtrlCommittedMenu.visible(false);
    }

    if (FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance()))
    {
        element.design().caption("@Proj:CostRecognition");
        Identification_ProjId.label("@Proj:RevenueProject");
        Identification_M_periodTo.label("@Proj:RevRecDate");
    }
}

void setButtonAcces()
{
    boolean isProjEstimatesToRevRecRenameFeatureEnabled = FeatureStateProvider::isFeatureEnabled(ProjEstimatesToRevRecRenameFeature::instance());
    if (projControlPeriod.PeriodStatus == ProjControlPeriodStatus::Created)
    {
        if (isProjEstimatesToRevRecRenameFeatureEnabled)
        {
            ctrlActualV2.enabled(true);
        }
        else
        {
            ctrlActual.enabled(true);
        }
        ctrlCheck.enabled(true);
        ctrlUpdate.enabled(true);
        if (projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::RemainingEstimate ||//Remaining budget
            projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::WorkProgressPercentage)//Total budget - Actual
        {
            ctrlEstimate.enabled(true);
        }
        else
        {
            ctrlEstimate.enabled(false);
        }
    }
    else
    {
        if (isProjEstimatesToRevRecRenameFeatureEnabled)
        {
            ctrlActualV2.enabled(true);
        }
        else
        {
            ctrlActual.enabled(true);
        }
        ctrlCheck.enabled(false);
        ctrlUpdate.enabled(false);
        ctrlEstimate.enabled(false);
    }

    if (projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::RemainingEstimate ||//Remaining budget
        projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::TotalEstimate)//Total cost - Actual
    {
        if (isProjEstimatesToRevRecRenameFeatureEnabled)
        {
            ctrlCommittedV2.enabled(true);
        }
        else
        {
            ctrlCommitted.enabled(true);
        }

        ctrlCommittedAcc.enabled(true);
    }
    else
    {
        if (isProjEstimatesToRevRecRenameFeatureEnabled)
        {
            ctrlCommittedV2.enabled(true);
        }
        else
        {
            ctrlCommitted.enabled(true);
        }
        ctrlCommittedAcc.enabled(false);
    }

    if (projControlPeriodCostGroup.ErrorText)
    {
        ctrlErrorText.enabled(true);
    }
    else
    {
        ctrlErrorText.enabled(false);
    }
}

void setFieldAccess()
{
    if (projControlPeriod.PeriodStatus  == ProjControlPeriodStatus::Posted      ||
        projControlPeriod.PeriodStatus  == ProjControlPeriodStatus::Reversal    ||
        projControlPeriod.Elimination)
    {
        projControlPeriodCostGroup_ds.allowEdit(false);
    }
    else
    {
        projControlPeriodCostGroup_ds.allowEdit(true);
        element.enableFields();
    }
}

void showFields()
{
    boolean showForecast;
    boolean showCommitted;
    boolean showCommittedPeriodEnd;
    boolean showActualPeriodEnd;
    boolean showActual;

    toComplete.visible(projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::RemainingEstimate ||//Remaining budget
        projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::TotBudgetMinusActual ||//Total budget - Actual
        projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::WorkProgressPercentage);//Percentage complete based on budget and actual

    switch (projControlPeriodCostGroup.ctcMethod)
    {
        case ProjControlPeriodCostGroupCTCMethod::RemainingEstimate://Remaining budget
            showForecast = true;
            showCommitted = true;
            showCommittedPeriodEnd = true;
            showActualPeriodEnd = true;
            break;

        case ProjControlPeriodCostGroupCTCMethod::WorkProgressPercentage:
            showActual = true;
            break;
        case ProjControlPeriodCostGroupCTCMethod::TotBudgetMinusActual://Total budget - Actual
            showForecast = true;
            showActual = true;
            break;
    }

    ctrlCTCForecastCostAmount.visible(showForecast);
    ctrlCTCCommittedCostAmount.visible(showCommitted);
    ctrlCTCCommittedPeriodEndCostAmount.visible(showCommittedPeriodEnd);
    ctrlCTCActualPeriodEndCostAmount.visible(showActualPeriodEnd);
    ctrlCTCActuelCostAmount.visible(showActual);

    ctrlQTCForecastCostUnit.visible(showForecast);
    ctrlQTCCommittedCostUnit.visible(showCommitted);
    ctrlQTCCommittedPeriodEndCostUnit.visible(showCommittedPeriodEnd);
    ctrlQTCActualPeriodEndCostUnit.visible(showActualPeriodEnd);
    ctrlQTCActuelCostUnit.visible(showActual);

    totalEstimate.visible(projControlPeriodCostGroup.ctcMethod == ProjControlPeriodCostGroupCTCMethod::TotalEstimate);//Total cost - Actual

    ProjCompletePrincip completePrincip;
    ProjMatchingPrincip matchingPrincip;
    ProjSalesPriceMatchingPrincip salesPriceMatchingPrincip;

    if (ProjMultipleContractLinesForProjectFeatureHelper::isFeatureEnabled())
    {
        ProjTable projTableLocal = ProjTable::find(projControlPeriod.ProjId);
        ProjRevenueProfile projRevenueProfile = ProjRevRecHelper::getRevProfile(projTableLocal, ProjContractBillingMethod::FixedPrice);
        completePrincip = projRevenueProfile.CompletePrinciple;
        matchingPrincip = projRevenueProfile.MatchingPrinciple;
        salesPriceMatchingPrincip = projRevenueProfile.SalesPriceMatchingPrinciple;
    }
    else
    {
        ProjGroup projGroup = projControlPeriod.projGroup();
        completePrincip = projGroup.CompletePrincip;
        matchingPrincip = projGroup.MatchingPrincip;
        salesPriceMatchingPrincip = projGroup.SalesPriceMatchingPrincip;
    }

    ctrlContract.visible((completePrincip == ProjCompletePrincip::CompletedPercentage || completePrincip == ProjCompletePrincip::CompletedContract) &&
        matchingPrincip == ProjMatchingPrincip::SalesValue &&
        salesPriceMatchingPrincip == ProjSalesPriceMatchingPrincip::MarkupPercentEstimateLine);
}

void linkActive()
{
    super();

    switch (element.args().record().TableId)
    {
        case (tableNum(ProjControlPeriodTable)) :

            projControlPeriod = element.args().record();
            break;
    }
}

int active()
{
    int ret;;

    ret = super();

    element.setFieldAccess();
    element.showFields();
    element.setButtonAcces();

    return ret;
}

public void modified()
{
    super();

    projControlPeriodCostGroup_ds.write();
    element.enableFields();
    element.showFields();
    element.setButtonAcces();
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = projControlPeriodCostGroup.checkCTCMethod(projControlPeriod.ModelId);
    }

    return ret;
}

void clicked()
{
    Args    args    = new Args();

    args.caller(element);
    args.record(projControlPeriodCostGroup);

    super();

    switch (projControlPeriodCostGroup.costType())
    {
        case ProjCostType::Hour : new MenuFunction(menuitemDisplayStr(ProjForecastEmpl), MenuItemType::Display).run(args);  break;
        case ProjCostType::Cost: new MenuFunction(menuitemDisplayStr(ProjForecastCost), MenuItemType::Display).run(args);  break;
        case ProjCostType::Item: new MenuFunction(menuitemDisplayStr(ForecastSales), MenuItemType::Display).run(args);   break;
        default:
    }
}

void clicked()
{
    ProjControlPeriodTable  projControlPeriodTable;
    Args                    args = new Args(this);
    ProjControlPeriod       pcP;

    super();

    projControlPeriodTable      = projControlPeriodCostGroup.projControlPeriod();
    args.record(projControlPeriodTable);

    pcP  = ProjControlPeriod::new_ProjControlPeriodic(ProjControlType::Update,args);
    pcP.runOperation();
    element.bookRefresh();
}
